(308, 'Fonctionnalité : Contact criminel', 'Vous avez un contact fiable et de confiance qui agit comme votre lien avec un réseau d\'autres criminels.', 7, 'background', 5, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(310, 'Maîtrise des outils', 'Vous maîtrisez les outils de voleur et un type d\'outils de jeu de votre choix.', 7, 'background', 6, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(313, 'Fonctionnalité : Connaissance des rues', 'Vous connaissez les rues de votre ville comme le dos de votre main.', 7, 'background', 6, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(315, 'Maîtrise des outils', 'Vous maîtrisez les outils d\'herboriste.', 7, 'background', 7, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(318, 'Fonctionnalité : Découverte', 'Votre étude et votre méditation vous ont donné un aperçu d\'un mystère important.', 7, 'background', 7, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(320, 'Maîtrise des outils', 'Vous maîtrisez un type d\'outils d\'artisan de votre choix et les véhicules terrestres.', 7, 'background', 8, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(322, 'Fonctionnalité : Réputation rustique', 'Vous pouvez vous reposer gratuitement dans n\'importe quel établissement où vous êtes reconnu.', 7, 'background', 8, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
(324, 'Maîtrise des outils', 'Vous maîtrisez les outils de navigateur et les véhicules aquatiques.', 7, 'background', 9, 1, 1, '2025-09-21 19:46:09', '2025-09-21 19:46:09'),
