(29, 1, 'char_creation_68d2f9687d25c8.02535943', 8, '{\"age\": \"19\", \"eyes\": \"Vert\", \"hair\": \"Long, frisé, Noir ébène\", \"name\": \"Barda\", \"skin\": \"Violette\", \"bonds\": \"eee\", \"flaws\": \"rrrrr\", \"level\": 1, \"speed\": 9, \"height\": \"1.69\", \"ideals\": \"zzzz\", \"weight\": \"51\", \"wisdom\": 10, \"race_id\": 19, \"charisma\": 14, \"class_id\": 2, \"strength\": 10, \"alignment\": \"Chaotique Neutre\", \"backstory\": \"Fille du démon, fille du son. \\r\\nTu joue et enjoue, te cache dans le houx\", \"dexterity\": 14, \"armor_class\": 12, \"constitution\": 14, \"fixed_skills\": [\"Acrobaties\", \"Représentation\"], \"intelligence\": 10, \"background_id\": 3, \"chosen_skills\": [\"Acrobaties\", \"Arcanes\", \"Discrétion\"], \"law_alignment\": \"Chaotique\", \"profile_photo\": \"uploads/profiles/profile_1_1758657112.png\", \"hit_points_max\": 10, \"class_option_id\": 6, \"fixed_languages\": [\"commun\", \"infernal\"], \"moral_alignment\": \"Neutre\", \"selected_skills\": [\"Acrobaties\", \"Représentation\", \"Acrobaties\", \"Arcanes\", \"Discrétion\"], \"chosen_languages\": [], \"class_option_type\": \"Collège\", \"experience_points\": 0, \"proficiency_bonus\": 2, \"class_option_table\": \"bard_colleges\", \"hit_points_current\": 10, \"personality_traits\": \"aaaa\", \"selected_languages\": [\"commun\", \"infernal\"]}', '2025-09-23 19:47:52', '2025-09-23 19:53:18'),
