INSERT INTO `classes` (`id`, `name`, `hit_dice`, `hit_point_bonus`, `armor_proficiencies`, `weapon_proficiencies`, `tool_proficiencies`, `saving_throws`, `skill_count`, `skill_choices`, `starting_equipment`, `description`, `created_at`, `updated_at`) VALUES
(1, 'Barbare', '1d12', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '', 'Force, Constitution', 2, 'Athlétisme, Dressage, Intimidation, Nature, Perception, Survie', '(a) une hache à deux mains ou (b) n\'importe quelle arme de guerre de corps à corps\n(a) deux hachettes ou (b) n\'importe quelle arme courante\nun sac d\'explorateur et quatre javelines', NULL, '2025-09-12 20:16:12', '2025-09-12 21:44:47'),
(2, 'Barde', '1d8', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\"]', '[\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\"]', '[\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Dextérité, Charisme', 3, 'Acrobaties, Arcanes, Athlétisme, Discrétion, Dressage, Escamotage, Histoire, Intimidation, Intuition, Investigation, Médecine, Nature, Perception, Persuasion, Religion, Représentation, Survie, Tromperie', '(a) une rapière, (b) une épée longue ou (c) n\'importe quelle arme courante\n(a) un sac de diplomate ou (b) un sac d\'artiste\n(a) un luth ou (b) n\'importe quel autre instrument de musique\nune armure de cuir et une dague', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(3, 'Clerc', '1d8', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Sagesse, Charisme', 2, 'Histoire, Intuition, Médecine, Persuasion, Religion', '(a) une masse d\'armes ou (b) un marteau de guerre (si vous le maîtrisez)\n(a) une armure d\'écailles ou (b) une armure de cuir ou (c) une cotte de mailles (si vous la maîtrisez)\n(a) une arbalète légère et 20 carreaux ou (b) une arme courante\n(a) un sac d\'ecclésiastique ou (b) un sac d\'explorateur\nun bouclier et un symbole sacré', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(4, 'Druide', '1d8', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Intelligence, Sagesse', 2, 'Arcanes, Dressage, Intuition, Médecine, Nature, Perception, Religion, Survie', '(a) un bouclier en bois ou (b) une arme courante\n(a) un cimeterre ou (b) une arme courante de corps à corps\nune armure de cuir, un sac d\'explorateur et un focaliseur druidique', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(5, 'Ensorceleur', '1d6', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\"]', '[\"Armes simples\",\"Armes \\u00e0 distance\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Constitution, Charisme', 2, 'Arcanes, Intimidation, Intuition, Persuasion, Religion, Tromperie', '(a) une arbalète légère et 20 carreaux ou (b) n\'importe quelle arme courante\n(a) une sacoche à composantes ou (b) un focaliseur arcanique\n(a) un sac d\'exploration souterraine ou (b) un sac d\'explorateur\ndeux dagues', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(6, 'Guerrier', '1d10', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Armure lourde\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Force, Constitution', 2, 'Acrobaties, Athlétisme, Dressage, Histoire, Intimidation, Intuition, Perception, Survie', '(a) une cotte de mailles ou (b) une armure de cuir, un arc long et 20 flèches\n(a) une arme de guerre et un bouclier ou (b) deux armes de guerre\n(a) une arbalète légère et 20 carreaux ou (b) deux hachettes\n(a) un sac d\'exploration souterraine ou (b) un sac d\'explorateur', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(7, 'Magicien', '1d6', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\"]', '[\"Armes simples\",\"Armes \\u00e0 distance\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Intelligence, Sagesse', 2, 'Arcanes, Histoire, Intuition, Investigation, Médecine, Religion', '(a) un bâton ou (b) une dague\n(a) une sacoche à composantes ou (b) un focaliseur arcanique\n(a) un sac d\'érudit ou (b) un sac d\'explorateur\nun grimoire', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(8, 'Moine', '1d8', 'Constitution', '[]', '[\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Force, Dextérité', 2, 'Acrobaties, Athlétisme, Discrétion, Histoire, Intuition, Religion', '(a) une épée courte ou (b) n\'importe quelle arme courante\n(a) un sac d\'exploration souterraine ou (b) un sac d\'explorateur\n10 fléchettes', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(9, 'Occultiste', '1d8', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\"]', '[\"Armes simples\",\"Armes \\u00e0 distance\"]', '', 'Sagesse, Charisme', 2, 'Arcanes, Histoire, Intimidation, Investigation, Nature, Religion, Tromperie', '(a) une arbalète légère et 20 carreaux ou (b) n\'importe quelle arme courante\n(a) une sacoche à composantes ou (b) un focaliseur arcanique\n(a) un sac d\'érudit ou (b) un sac d\'exploration souterraine\nune armure de cuir, n\'importe quelle arme courante, et deux dagues', NULL, '2025-09-12 20:16:12', '2025-09-12 21:44:47'),
(10, 'Paladin', '1d10', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Armure lourde\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Sagesse, Charisme', 2, 'Athlétisme, Intimidation, Intuition, Médecine, Persuasion, Religion', '(a) une arme de guerre et un bouclier ou (b) deux armes de guerre\n(a) cinq javelines ou (b) n\'importe quelle arme courante de corps à corps\n(a) un sac d\'ecclésiastique ou (b) un sac d\'explorateur\nune cotte de mailles et un symbole sacré', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(11, 'Rôdeur', '1d10', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\",\"Armure interm\\u00e9diaire\",\"Bouclier\"]', '[\"Armes courantes\",\"Armes de guerre\",\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\",\"Armes d\'hast\",\"Armes de lancer\"]', '[\"Outils d\'artisan\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Force, Dextérité', 3, 'Athlétisme, Discrétion, Dressage, Intuition, Investigation, Nature, Perception, Survie', '(a) armure d\'écailles ou (b) armure de cuir\n(a) deux épées courtes ou (b) deux armes courantes de corps à corps\n(a) un sac d\'exploration souterraine ou (b) un sac d\'explorateur\nun arc long et un carquois avec 20 flèches', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49'),
(12, 'Roublard', '1d8', 'Constitution', '[\"Armure l\\u00e9g\\u00e8re\"]', '[\"Armes simples\",\"Armes \\u00e0 distance\",\"Armes de m\\u00eal\\u00e9e\"]', '[\"Outils de voleur\",\"Instruments de musique\",\"Jeux\",\"V\\u00e9hicules\"]', 'Dextérité, Intelligence', 4, 'Acrobaties, Athlétisme, Discrétion, Escamotage, Intimidation, Intuition, Investigation, Perception, Persuasion, Représentation, Tromperie', '(a) une rapière ou (b) une épée courte\n(a) un arc court et un carquois de 20 flèches ou (b) une épée courte\n(a) un sac de cambrioleur, (b) un sac d\'exploration souterraine ou (c) un sac d\'explorateur\nune armure de cuir, deux dagues et des outils de voleur', NULL, '2025-09-12 20:16:12', '2025-09-12 21:33:49');
INSERT INTO `class_archetypes` (`id`, `class_id`, `name`, `description`, `level_1_feature`, `level_2_feature`, `level_3_feature`, `level_6_feature`, `level_7_feature`, `level_8_feature`, `level_9_feature`, `level_10_feature`, `level_11_feature`, `level_13_feature`, `level_14_feature`, `level_15_feature`, `level_17_feature`, `level_18_feature`, `level_20_feature`, `created_at`, `updated_at`) VALUES
(1, 1, 'Voie du Berserker', 'Pour certains barbares, la rage est un moyen d\'atteindre un état de fureur pure et destructrice. Ces barbares sont connus sous le nom de berserkers.', NULL, NULL, 'Frenésie : Quand vous entrez en rage, vous pouvez également entrer dans un état de frénésie. Tant que vous êtes en frénésie, vous pouvez effectuer une attaque de mêlée supplémentaire en tant qu\'action bonus lors de votre tour. Cependant, à la fin de votre rage, vous souffrez d\'un niveau d\'épuisement.', 'Instinct sans peur : Vous ne pouvez pas être charmé ou terrorisé tant que vous êtes en rage. Si vous êtes charmé ou terrorisé quand vous entrez en rage, l\'effet est suspendu pendant la durée de votre rage.', NULL, NULL, NULL, 'Intimidation : Vous pouvez utiliser votre action pour terroriser quelqu\'un. Choisissez une créature que vous pouvez voir dans un rayon de 9 mètres. Elle doit réussir un jet de sauvegarde de Sagesse contre un DD égal à 8 + votre bonus de maîtrise + votre modificateur de Charisme. Si elle échoue, elle est terrorisée par vous pendant 1 minute. La créature terrorisée peut répéter le jet de sauvegarde à la fin de chacun de ses tours, mettant fin à l\'effet sur elle-même en cas de réussite.', NULL, NULL, 'Rage implacable : Si vous tombez à 0 points de vie pendant votre rage, vous pouvez faire un jet de sauvegarde de Constitution. Si vous réussissez, vous tombez à 1 point de vie à la place. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-16 19:11:42', '2025-10-13 18:55:52'),
(2, 1, 'Voie du Totem', 'Les barbares qui empruntent la voie du totem s\'inspirent des animaux pour améliorer leurs capacités de combat. Ces barbares apprennent à invoquer les esprits d\'animaux pour les guider et les protéger.', NULL, NULL, 'Esprit animal : Vous gagnez l\'aptitude de l\'esprit animal de votre choix. Vous pouvez choisir parmi les options suivantes : Esprit de l\'ours, Esprit de l\'aigle, Esprit du loup.', 'Attribut de l\'animal : Vous gagnez l\'aptitude de l\'attribut de l\'animal de votre choix. Vous pouvez choisir parmi les options suivantes : Attribut de l\'ours, Attribut de l\'aigle, Attribut du loup.', NULL, NULL, NULL, 'Esprit de la nature : Vous gagnez l\'aptitude de l\'esprit de la nature de votre choix. Vous pouvez choisir parmi les options suivantes : Esprit de l\'ours, Esprit de l\'aigle, Esprit du loup.', NULL, NULL, 'Attribut de la nature : Vous gagnez l\'aptitude de l\'attribut de la nature de votre choix. Vous pouvez choisir parmi les options suivantes : Attribut de l\'ours, Attribut de l\'aigle, Attribut du loup.', NULL, NULL, NULL, NULL, '2025-09-16 19:11:42', '2025-10-13 18:55:52'),
(3, 1, 'Voie de la magie sauvage', 'Votre rage est alimentée par la magie sauvage qui coule dans vos veines. Cette magie primitive et imprévisible peut se manifester de manière inattendue, créant des effets magiques chaotiques.', NULL, NULL, 'Magie sauvage : Quand vous entrez en rage, vous pouvez lancer un dé pour déterminer un effet magique aléatoire. Ces effets peuvent inclure des dégâts supplémentaires, des téléportations, des transformations temporaires, ou d\'autres effets magiques imprévisibles.', 'Instinct magique : Vous développez un instinct pour la magie sauvage. Vous pouvez utiliser votre réaction pour relancer un dé de magie sauvage une fois par rage.', NULL, NULL, NULL, 'Magie contrôlée : Vous apprenez à mieux contrôler votre magie sauvage. Vous pouvez choisir d\'ignorer un effet de magie sauvage et en lancer un autre à la place.', NULL, NULL, 'Magie maîtrisée : Votre magie sauvage devient plus puissante et plus prévisible. Vous gagnez un avantage sur tous vos jets de magie sauvage et pouvez lancer des sorts de magie sauvage supplémentaires.', NULL, NULL, NULL, NULL, '2025-09-16 19:19:22', '2025-10-13 18:55:52'),
(4, 2, 'Collège du Savoir', 'Les bardes du Collège du Savoir sont des érudits et des chercheurs qui utilisent leur magie pour découvrir des secrets et des connaissances perdues. Ils sont des maîtres de l\'information et de la divination.', NULL, NULL, 'Inspiration de compétence : Quand vous utilisez votre inspiration bardique, vous pouvez choisir de donner un dé d\'inspiration à une créature alliée. Cette créature peut utiliser ce dé pour ajouter le résultat à un test de compétence qu\'elle effectue dans la minute qui suit. Vous pouvez utiliser cette aptitude un nombre de fois égal à votre modificateur de Charisme (minimum 1) entre deux repos longs.', 'Expertise : Vous pouvez choisir deux compétences que vous maîtrisez. Votre bonus de maîtrise pour ces compétences est doublé pour tous les tests de compétence que vous effectuez avec elles.', NULL, NULL, NULL, NULL, NULL, NULL, 'Secrets magiques : Vous apprenez deux sorts de n\'importe quelle classe. Un sort que vous choisissez doit être d\'un niveau que vous pouvez lancer, comme indiqué dans le tableau du barde. Les sorts choisis comptent comme des sorts de barde pour vous, mais ils ne comptent pas dans le nombre de sorts de barde que vous connaissez.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(5, 2, 'Collège de la Gloire', 'Les bardes du Collège de la Gloire sont des héros légendaires qui inspirent les autres par leurs exploits et leurs récits. Ils sont des champions de la justice et des protecteurs des faibles.', NULL, NULL, 'Inspiration héroïque : Quand vous utilisez votre inspiration bardique, vous pouvez choisir de donner un dé d\'inspiration à une créature alliée. Cette créature peut utiliser ce dé pour ajouter le résultat à un jet d\'attaque, un test de caractéristique, ou un jet de sauvegarde qu\'elle effectue dans la minute qui suit. Vous pouvez utiliser cette aptitude un nombre de fois égal à votre modificateur de Charisme (minimum 1) entre deux repos longs.', 'Récit héroïque : Vous pouvez utiliser votre action pour commencer un récit héroïque qui dure jusqu\'à la fin de votre prochain tour. Pendant ce récit, vous et tous les alliés amicaux dans un rayon de 9 mètres gagnez un avantage aux jets d\'attaque et aux tests de caractéristique.', NULL, NULL, NULL, NULL, NULL, NULL, 'Légende vivante : Vous devenez une légende vivante. Votre réputation vous précède, et vous inspirez l\'admiration et la crainte. Vous gagnez un avantage aux tests de Charisme (Intimidation) et (Persuasion) contre les créatures qui vous reconnaissent.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(6, 2, 'Collège des Swords', 'Les bardes du Collège des Swords sont des combattants élégants qui combinent la magie et l\'escrime. Ils sont des duellistes redoutables et des artistes martiaux.', NULL, NULL, 'Style de combat : Vous apprenez un style de combat de votre choix parmi : Duel, Défense, ou Archerie. Vous ne pouvez pas prendre un style de combat plus d\'une fois, même si vous obtenez plus tard l\'occasion de le faire.', 'Attaque supplémentaire : Vous pouvez attaquer deux fois, au lieu d\'une, chaque fois que vous effectuez l\'action Attaquer lors de votre tour.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître du duel : Vous maîtrisez l\'art du duel. Quand vous êtes dans un rayon de 1,5 mètre d\'une créature et qu\'aucune autre créature n\'est dans un rayon de 1,5 mètre de vous, vous avez un avantage aux jets d\'attaque contre cette créature.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(7, 2, 'Collège de la Valor', 'Les bardes du Collège de la Valor sont des guerriers-poètes qui inspirent leurs alliés au combat. Ils sont des leaders naturels et des tacticiens brillants.', NULL, NULL, 'Maîtrise d\'armures et d\'armes : Vous maîtrisez les armures intermédiaires, les boucliers et les armes martiales.', 'Attaque supplémentaire : Vous pouvez attaquer deux fois, au lieu d\'une, chaque fois que vous effectuez l\'action Attaquer lors de votre tour.', NULL, NULL, NULL, NULL, NULL, NULL, 'Inspiration de combat : Quand vous utilisez votre inspiration bardique, vous pouvez choisir de donner un dé d\'inspiration à une créature alliée. Cette créature peut utiliser ce dé pour ajouter le résultat à un jet d\'attaque ou de dégâts qu\'elle effectue dans la minute qui suit. Vous pouvez utiliser cette aptitude un nombre de fois égal à votre modificateur de Charisme (minimum 1) entre deux repos longs.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(8, 2, 'Collège des Whispers', 'Les bardes du Collège des Whispers sont des maîtres de l\'espionnage et de la manipulation. Ils utilisent leur magie pour infiltrer, espionner et manipuler les autres.', NULL, NULL, 'Mots de pouvoir : Vous apprenez le sort suggestion. Vous pouvez le lancer sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', 'Espion : Vous gagnez la capacité de vous déguiser parfaitement. Vous pouvez utiliser votre action pour prendre l\'apparence d\'une créature humanoïde de votre taille que vous avez vue. Cette transformation dure 1 heure ou jusqu\'à ce que vous la mettiez fin volontairement.', NULL, NULL, NULL, NULL, NULL, NULL, 'Manipulation mentale : Vous pouvez utiliser votre action pour tenter de contrôler l\'esprit d\'une créature que vous pouvez voir dans un rayon de 9 mètres. La créature doit réussir un jet de sauvegarde de Sagesse contre un DD égal à 8 + votre bonus de maîtrise + votre modificateur de Charisme, ou être charmée par vous pendant 1 minute.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(9, 2, 'Collège des Glamour', 'Les bardes du Collège des Glamour sont des maîtres de l\'enchantement et de la séduction. Ils utilisent leur magie pour charmer, enchanter et manipuler les émotions.', NULL, NULL, 'Charme de séduction : Vous gagnez la capacité de charmer les autres par votre présence. Quand vous utilisez votre action pour commencer une performance, vous pouvez choisir jusqu\'à trois créatures dans un rayon de 9 mètres qui peuvent vous voir et vous entendre. Chaque créature doit réussir un jet de sauvegarde de Sagesse contre un DD égal à 8 + votre bonus de maîtrise + votre modificateur de Charisme, ou être charmée par vous pendant 1 minute.', 'Enchantement : Vous pouvez utiliser votre action pour lancer le sort suggestion sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de l\'enchantement : Vous devenez un maître de l\'enchantement. Vous pouvez lancer le sort domination de personne une fois sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 16:36:27', '2025-09-18 16:36:27'),
(11, 3, 'Domaine de la Vie', 'Le domaine de la Vie se concentre sur la préservation de la vie et la guérison. Les clercs de ce domaine sont des guérisseurs naturels et des protecteurs de la vie.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Vie : Vous pouvez utiliser votre action pour toucher une créature et la soigner d\'un nombre de points de vie égal à 2 + votre niveau de clerc. Vous pouvez utiliser cette aptitude un nombre de fois égal à votre modificateur de Sagesse (minimum 1) entre deux repos longs.', 'Canalisation de Divinité : Préservation de la Vie : En tant qu\'action, vous présentez votre symbole sacré et invoquez une énergie curative qui se répand sur vous et toutes les créatures amicales dans un rayon de 9 mètres. Chaque créature (vous y compris) récupère un nombre de points de vie égal à la moitié de votre niveau de clerc (minimum 1).', NULL, 'Bénédiction suprême : Quand vous utilisez votre aptitude Bénédiction de la Vie, la créature récupère le maximum de points de vie possible pour cette aptitude.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de la Vie : Vous avez appris à utiliser votre magie pour maintenir la vie même dans les situations les plus désespérées. Vous pouvez lancer le sort résurrection sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(12, 3, 'Domaine de la Lumière', 'Le domaine de la Lumière se concentre sur la lumière, la vérité et la révélation. Les clercs de ce domaine sont des porteurs de lumière et des révélateurs de vérité.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Lumière : Vous pouvez utiliser votre action pour toucher une créature et la bénir avec la lumière divine. La créature gagne un avantage aux jets d\'attaque contre les créatures des ténèbres et aux jets de sauvegarde contre les sorts de ténèbres.', 'Canalisation de Divinité : Radiance du Soleil : En tant qu\'action, vous présentez votre symbole sacré et invoquez une lumière aveuglante. Chaque créature hostile dans un rayon de 9 mètres doit réussir un jet de sauvegarde de Constitution contre votre DD de sort de clerc, ou être aveuglée pendant 1 minute.', NULL, 'Amélioration de sort : Vous pouvez lancer le sort boule de feu comme un sort de clerc. Il compte comme un sort de clerc pour vous, mais il ne compte pas dans le nombre de sorts de clerc que vous connaissez.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Corona de Lumière : Vous êtes entouré d\'une aura de lumière divine. Vous émettez une lumière vive dans un rayon de 9 mètres et une lumière faible dans un rayon de 18 mètres. Les créatures des ténèbres ont un désavantage aux jets d\'attaque contre vous.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(13, 3, 'Domaine de la Nature', 'Le domaine de la Nature se concentre sur la nature, les animaux et les éléments. Les clercs de ce domaine sont des protecteurs de la nature et des maîtres des éléments.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Nature : Vous pouvez utiliser votre action pour toucher une créature et la bénir avec la force de la nature. La créature gagne un avantage aux jets de sauvegarde contre les sorts et effets magiques.', 'Canalisation de Divinité : Maîtrise de la Nature : En tant qu\'action, vous présentez votre symbole sacré et invoquez la puissance de la nature. Vous pouvez lancer le sort parler avec les animaux ou le sort parler avec les plantes sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de sort : Vous pouvez lancer le sort mur de feu comme un sort de clerc. Il compte comme un sort de clerc pour vous, mais il ne compte pas dans le nombre de sorts de clerc que vous connaissez.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de la Nature : Vous avez appris à utiliser votre magie pour contrôler les éléments. Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(14, 3, 'Domaine de la Tempête', 'Le domaine de la Tempête se concentre sur la puissance destructrice des tempêtes et des éléments. Les clercs de ce domaine sont des maîtres de la foudre et du tonnerre.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Tempête : Vous pouvez utiliser votre action pour toucher une créature et la bénir avec la puissance de la tempête. La créature gagne un avantage aux jets d\'attaque avec les armes de foudre et de tonnerre.', 'Canalisation de Divinité : Destructeur de Tempête : En tant qu\'action, vous présentez votre symbole sacré et invoquez une tempête destructrice. Chaque créature hostile dans un rayon de 9 mètres doit réussir un jet de sauvegarde de Dextérité contre votre DD de sort de clerc, ou subir 2d8 dégâts de foudre.', NULL, 'Amélioration de sort : Vous pouvez lancer le sort éclair comme un sort de clerc. Il compte comme un sort de clerc pour vous, mais il ne compte pas dans le nombre de sorts de clerc que vous connaissez.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de la Tempête : Vous avez appris à utiliser votre magie pour invoquer des tempêtes dévastatrices. Vous pouvez lancer le sort tempête de feu sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(15, 3, 'Domaine de la Guerre', 'Le domaine de la Guerre se concentre sur la guerre, la stratégie et le combat. Les clercs de ce domaine sont des guerriers divins et des tacticiens.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Guerre : Vous pouvez utiliser votre action pour toucher une créature et la bénir avec la puissance de la guerre. La créature gagne un avantage aux jets d\'attaque et aux jets de dégâts.', 'Canalisation de Divinité : Guerrier Divin : En tant qu\'action, vous présentez votre symbole sacré et invoquez la puissance divine du combat. Vous pouvez faire une attaque d\'arme en action bonus.', NULL, 'Amélioration de sort : Vous pouvez lancer le sort fléau comme un sort de clerc. Il compte comme un sort de clerc pour vous, mais il ne compte pas dans le nombre de sorts de clerc que vous connaissez.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de la Guerre : Vous avez appris à utiliser votre magie pour devenir un guerrier divin. Vous pouvez lancer le sort fléau sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(16, 3, 'Domaine de la Mort', 'Le domaine de la Mort se concentre sur la mort, les morts-vivants et la nécromancie. Les clercs de ce domaine sont des maîtres de la mort et des nécromanciens.', 'Maîtrise d\'armures lourdes : Vous maîtrisez les armures lourdes. Bénédiction de la Mort : Vous pouvez utiliser votre action pour toucher une créature et la bénir avec la puissance de la mort. La créature gagne un avantage aux jets d\'attaque contre les morts-vivants.', 'Canalisation de Divinité : Maîtrise de la Mort : En tant qu\'action, vous présentez votre symbole sacré et invoquez la puissance de la mort. Vous pouvez lancer le sort animation des morts sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de sort : Vous pouvez lancer le sort cercle de mort comme un sort de clerc. Il compte comme un sort de clerc pour vous, mais il ne compte pas dans le nombre de sorts de clerc que vous connaissez.', NULL, 'Amélioration de caractéristique divine : Vous pouvez augmenter votre Force ou votre Sagesse de 2, ou deux autres caractéristiques de 1. Vous ne pouvez pas augmenter une caractéristique au-dessus de 20 avec cette aptitude.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maître de la Mort : Vous avez appris à utiliser votre magie pour contrôler la mort elle-même. Vous pouvez lancer le sort mort sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, '2025-09-18 18:05:14', '2025-09-18 18:05:14'),
(18, 4, 'Cercle de la Terre', 'Le Cercle de la Terre se concentre sur la magie de la terre et de la pierre. Les druides de ce cercle sont des maîtres de la géomancie et de la protection.', NULL, 'Maîtrise de la Terre : Vous pouvez lancer le sort résistance à la terre sans dépenser d\'emplacement de sort. Vous pouvez également lancer le sort protection contre l\'énergie (terre) sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de la Terre : Vous pouvez lancer le sort mur de pierre sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, 'Maîtrise de la Terre améliorée : Vous pouvez lancer le sort tremblement de terre sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître de la Terre : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(19, 4, 'Cercle de la Lune', 'Le Cercle de la Lune se concentre sur la transformation et la forme sauvage. Les druides de ce cercle sont des maîtres de la métamorphose et de la nature sauvage.', NULL, 'Forme sauvage améliorée : Vous pouvez vous transformer en bête d\'un défi supérieur. Vous pouvez également vous transformer en bête d\'un défi 1/4 ou moins sans dépenser d\'utilisation de forme sauvage.', NULL, 'Forme sauvage élémentaire : Vous pouvez vous transformer en élémentaire de terre, d\'air, de feu ou d\'eau. Vous pouvez utiliser cette aptitude une fois entre deux repos longs.', NULL, NULL, NULL, 'Forme sauvage supérieure : Vous pouvez vous transformer en bête d\'un défi 6 ou moins. Vous pouvez également vous transformer en bête d\'un défi 1/2 ou moins sans dépenser d\'utilisation de forme sauvage.', NULL, NULL, 'Forme sauvage archidruide : Vous pouvez vous transformer en bête d\'un défi 8 ou moins. Vous pouvez également vous transformer en bête d\'un défi 1 ou moins sans dépenser d\'utilisation de forme sauvage.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(20, 4, 'Cercle des Saisons', 'Le Cercle des Saisons se concentre sur le cycle des saisons et la magie temporelle. Les druides de ce cercle sont des maîtres du temps et des cycles naturels.', NULL, 'Maîtrise des Saisons : Vous pouvez lancer le sort résistance à la saison sans dépenser d\'emplacement de sort. Vous pouvez également lancer le sort protection contre l\'énergie (saison) sans dépenser d\'emplacement de sort.', NULL, 'Amélioration des Saisons : Vous pouvez lancer le sort mur de saison sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, 'Maîtrise des Saisons améliorée : Vous pouvez lancer le sort tempête de saison sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître des Saisons : Vous pouvez lancer le sort météore de saison sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(21, 4, 'Cercle des Étoiles', 'Le Cercle des Étoiles se concentre sur la magie stellaire et la divination. Les druides de ce cercle sont des maîtres de la navigation et de la prédiction.', NULL, 'Maîtrise des Étoiles : Vous pouvez lancer le sort résistance aux étoiles sans dépenser d\'emplacement de sort. Vous pouvez également lancer le sort protection contre l\'énergie (étoiles) sans dépenser d\'emplacement de sort.', NULL, 'Amélioration des Étoiles : Vous pouvez lancer le sort mur d\'étoiles sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, 'Maîtrise des Étoiles améliorée : Vous pouvez lancer le sort tempête d\'étoiles sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître des Étoiles : Vous pouvez lancer le sort météore d\'étoiles sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(22, 4, 'Cercle des Esprits', 'Le Cercle des Esprits se concentre sur la communication avec les esprits et les morts-vivants. Les druides de ce cercle sont des maîtres de la nécromancie naturelle.', NULL, 'Maîtrise des Esprits : Vous pouvez lancer le sort résistance aux esprits sans dépenser d\'emplacement de sort. Vous pouvez également lancer le sort protection contre l\'énergie (esprits) sans dépenser d\'emplacement de sort.', NULL, 'Amélioration des Esprits : Vous pouvez lancer le sort mur d\'esprits sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, 'Maîtrise des Esprits améliorée : Vous pouvez lancer le sort tempête d\'esprits sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître des Esprits : Vous pouvez lancer le sort météore d\'esprits sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(23, 4, 'Cercle des Rêves', 'Le Cercle des Rêves se concentre sur la magie onirique et la guérison. Les druides de ce cercle sont des maîtres du sommeil et de la restauration.', NULL, 'Maîtrise des Rêves : Vous pouvez lancer le sort résistance aux rêves sans dépenser d\'emplacement de sort. Vous pouvez également lancer le sort protection contre l\'énergie (rêves) sans dépenser d\'emplacement de sort.', NULL, 'Amélioration des Rêves : Vous pouvez lancer le sort mur de rêves sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, 'Maîtrise des Rêves améliorée : Vous pouvez lancer le sort tempête de rêves sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître des Rêves : Vous pouvez lancer le sort météore de rêves sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, '2025-09-18 18:30:51', '2025-09-18 18:30:51'),
(25, 5, 'Origine Draconique', 'L\'origine draconique se concentre sur la magie des dragons. Les ensorceleurs de cette origine sont liés au pouvoir ancestral des dragons et peuvent invoquer leur force.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Résistance draconique : Vous gagnez une résistance aux dégâts du type de votre ancêtre draconique. Langue draconique : Vous pouvez parler, lire et écrire le draconique.', NULL, NULL, 'Amélioration de la magie draconique : Quand vous lancez un sort qui inflige des dégâts du type de votre ancêtre draconique, vous ajoutez votre modificateur de Charisme aux dégâts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ailes draconiques : Vous gagnez des ailes de dragon temporaires. Vous pouvez utiliser votre action pour les faire apparaître ou disparaître. Quand elles sont déployées, vous avez une vitesse de vol égale à votre vitesse de marche.', NULL, NULL, 'Maître draconique : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(26, 5, 'Origine Sauvage', 'L\'origine sauvage se concentre sur la magie chaotique et imprévisible. Les ensorceleurs de cette origine sont des maîtres de la magie sauvage et peuvent invoquer des effets magiques inattendus.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Magie sauvage : Quand vous lancez un sort de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, 'Amélioration de la magie sauvage : Vous pouvez lancer le sort boule de feu sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise de la magie sauvage : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, NULL, 'Maître de la magie sauvage : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(27, 5, 'Origine Divine', 'L\'origine divine se concentre sur la magie divine et la connexion avec les dieux. Les ensorceleurs de cette origine sont des canaux de pouvoir divin et peuvent invoquer la puissance des dieux.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Résistance divine : Vous gagnez une résistance aux dégâts divins. Langue divine : Vous pouvez parler, lire et écrire le céleste.', NULL, NULL, 'Amélioration de la magie divine : Quand vous lancez un sort qui inflige des dégâts divins, vous ajoutez votre modificateur de Charisme aux dégâts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ailes divines : Vous gagnez des ailes divines temporaires. Vous pouvez utiliser votre action pour les faire apparaître ou disparaître. Quand elles sont déployées, vous avez une vitesse de vol égale à votre vitesse de marche.', NULL, NULL, 'Maître divin : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(28, 5, 'Origine Élémentaire', 'L\'origine élémentaire se concentre sur la magie des éléments. Les ensorceleurs de cette origine sont des maîtres des éléments et peuvent invoquer la puissance de la terre, de l\'air, du feu et de l\'eau.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Résistance élémentaire : Vous gagnez une résistance aux dégâts élémentaires. Langue élémentaire : Vous pouvez parler, lire et écrire le primordial.', NULL, NULL, 'Amélioration de la magie élémentaire : Quand vous lancez un sort qui inflige des dégâts élémentaires, vous ajoutez votre modificateur de Charisme aux dégâts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ailes élémentaires : Vous gagnez des ailes élémentaires temporaires. Vous pouvez utiliser votre action pour les faire apparaître ou disparaître. Quand elles sont déployées, vous avez une vitesse de vol égale à votre vitesse de marche.', NULL, NULL, 'Maître élémentaire : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(29, 5, 'Origine Abyssale', 'L\'origine abyssale se concentre sur la magie démoniaque et infernale. Les ensorceleurs de cette origine sont des canaux de pouvoir démoniaque et peuvent invoquer la puissance des démons.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Résistance abyssale : Vous gagnez une résistance aux dégâts abyssaux. Langue abyssale : Vous pouvez parler, lire et écrire l\'abyssal.', NULL, NULL, 'Amélioration de la magie abyssale : Quand vous lancez un sort qui inflige des dégâts abyssaux, vous ajoutez votre modificateur de Charisme aux dégâts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ailes abyssales : Vous gagnez des ailes abyssales temporaires. Vous pouvez utiliser votre action pour les faire apparaître ou disparaître. Quand elles sont déployées, vous avez une vitesse de vol égale à votre vitesse de marche.', NULL, NULL, 'Maître abyssal : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(30, 5, 'Origine Céleste', 'L\'origine céleste se concentre sur la magie angélique et divine. Les ensorceleurs de cette origine sont des canaux de pouvoir angélique et peuvent invoquer la puissance des anges.', 'Maîtrise de l\'armure : Vous maîtrisez les armures légères. Résistance céleste : Vous gagnez une résistance aux dégâts célestes. Langue céleste : Vous pouvez parler, lire et écrire le céleste.', NULL, NULL, 'Amélioration de la magie céleste : Quand vous lancez un sort qui inflige des dégâts célestes, vous ajoutez votre modificateur de Charisme aux dégâts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Ailes célestes : Vous gagnez des ailes célestes temporaires. Vous pouvez utiliser votre action pour les faire apparaître ou disparaître. Quand elles sont déployées, vous avez une vitesse de vol égale à votre vitesse de marche.', NULL, NULL, 'Maître céleste : Vous pouvez lancer le sort météore sans dépenser d\'emplacement de sort. Une fois que vous avez utilisé cette aptitude, vous ne pouvez plus l\'utiliser tant que vous n\'avez pas terminé un repos long.', NULL, '2025-09-18 18:47:11', '2025-09-18 18:47:11'),
(32, 6, 'Champion', 'L\'archétype Champion se concentre sur la maîtrise des armes et la précision au combat. Les guerriers de cet archétype sont des maîtres du combat et peuvent infliger des coups critiques dévastateurs.', NULL, NULL, 'Amélioration critique : Vos attaques d\'arme marquent un coup critique sur un 19 ou un 20. De plus, vous gagnez un style de combat supplémentaire.', NULL, 'Remarquable athlète : Vous pouvez ajouter la moitié de votre bonus de maîtrise (arrondi vers le bas) à tout test de Force, Dextérité ou Constitution que vous faites et qui n\'utilise pas déjà votre bonus de maîtrise.', NULL, NULL, 'Style de combat supplémentaire : Vous gagnez un style de combat supplémentaire. Vous ne pouvez pas prendre le même style de combat plus d\'une fois, même si vous avez d\'autres occasions de le faire.', NULL, NULL, NULL, 'Amélioration critique supérieure : Vos attaques d\'arme marquent un coup critique sur un 18, 19 ou 20.', NULL, 'Survivant : Vous atteignez la perfection physique qui vous permet de survivre aux blessures les plus graves. Vous récupérez 5 + votre modificateur de Constitution (minimum 1) points de vie au début de votre tour si vous n\'avez pas plus de la moitié de vos points de vie maximum.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(33, 6, 'Maître d\'armes', 'L\'archétype Maître d\'armes se concentre sur la maîtrise de toutes les armes et armures. Les guerriers de cet archétype sont des experts en équipement et peuvent utiliser n\'importe quelle arme avec efficacité.', NULL, NULL, 'Maîtrise d\'armes : Vous gagnez la maîtrise de toutes les armes et armures. De plus, vous gagnez un style de combat supplémentaire.', NULL, 'Amélioration d\'arme : Vous pouvez utiliser votre action pour améliorer une arme que vous tenez. L\'arme gagne un bonus de +1 aux jets d\'attaque et de dégâts pendant 1 minute.', NULL, NULL, 'Style de combat supplémentaire : Vous gagnez un style de combat supplémentaire. Vous ne pouvez pas prendre le même style de combat plus d\'une fois, même si vous avez d\'autres occasions de le faire.', NULL, NULL, NULL, 'Maîtrise d\'arme supérieure : Vous pouvez utiliser votre action pour améliorer une arme que vous tenez. L\'arme gagne un bonus de +2 aux jets d\'attaque et de dégâts pendant 1 minute.', NULL, 'Maître d\'arme légendaire : Vous pouvez utiliser votre action pour améliorer une arme que vous tenez. L\'arme gagne un bonus de +3 aux jets d\'attaque et de dégâts pendant 1 minute.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(34, 6, 'École de bataille', 'L\'archétype École de bataille se concentre sur la tactique et la stratégie au combat. Les guerriers de cet archétype sont des tacticiens et peuvent utiliser des manœuvres spéciales.', NULL, NULL, 'Manœuvres de combat : Vous apprenez trois manœuvres de votre choix. Vous gagnez un dé de manœuvre (d8). Vous récupérez tous vos dés de manœuvre dépensés quand vous terminez un repos court ou long.', NULL, 'Connaissance de la guerre : Vous gagnez la maîtrise de deux compétences de votre choix parmi : Animal Handling, History, Insight, Intimidation, Perception et Survival.', NULL, NULL, 'Manœuvres supplémentaires : Vous apprenez deux manœuvres supplémentaires de votre choix. Vous gagnez également un dé de manœuvre supplémentaire.', NULL, NULL, NULL, 'Amélioration de manœuvre : Vos dés de manœuvre deviennent des d10. Au niveau 18, ils deviennent des d12.', NULL, 'Maître de guerre : Vous pouvez utiliser votre action pour donner l\'ordre d\'attaquer à un allié dans un rayon de 9 mètres. L\'allié peut immédiatement faire une attaque d\'arme en réaction.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(35, 6, 'École de cavalerie', 'L\'archétype École de cavalerie se concentre sur le combat monté et la mobilité. Les guerriers de cet archétype sont des cavaliers experts et peuvent utiliser des tactiques de cavalerie.', NULL, NULL, 'Maîtrise de la cavalerie : Vous gagnez la maîtrise des animaux de guerre. De plus, vous gagnez un style de combat supplémentaire.', NULL, 'Tactiques de cavalerie : Vous pouvez utiliser votre action pour donner l\'ordre de charger à votre monture. Votre monture peut se déplacer jusqu\'à sa vitesse et faire une attaque d\'arme.', NULL, NULL, 'Style de combat supplémentaire : Vous gagnez un style de combat supplémentaire. Vous ne pouvez pas prendre le même style de combat plus d\'une fois, même si vous avez d\'autres occasions de le faire.', NULL, NULL, NULL, 'Maîtrise de la cavalerie supérieure : Vous pouvez utiliser votre action pour donner l\'ordre de charger à votre monture. Votre monture peut se déplacer jusqu\'à deux fois sa vitesse et faire une attaque d\'arme.', NULL, 'Cavalier légendaire : Vous pouvez utiliser votre action pour donner l\'ordre de charger à votre monture. Votre monture peut se déplacer jusqu\'à trois fois sa vitesse et faire une attaque d\'arme.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(36, 6, 'École de défense', 'L\'archétype École de défense se concentre sur la protection et la défense. Les guerriers de cet archétype sont des protecteurs et peuvent utiliser des tactiques défensives.', NULL, NULL, 'Maîtrise de la défense : Vous gagnez la maîtrise des boucliers. De plus, vous gagnez un style de combat supplémentaire.', NULL, 'Tactiques défensives : Vous pouvez utiliser votre action pour adopter une posture défensive. Vous gagnez un bonus de +2 à votre CA jusqu\'au début de votre prochain tour.', NULL, NULL, 'Style de combat supplémentaire : Vous gagnez un style de combat supplémentaire. Vous ne pouvez pas prendre le même style de combat plus d\'une fois, même si vous avez d\'autres occasions de le faire.', NULL, NULL, NULL, 'Maîtrise de la défense supérieure : Vous pouvez utiliser votre action pour adopter une posture défensive. Vous gagnez un bonus de +3 à votre CA jusqu\'au début de votre prochain tour.', NULL, 'Défenseur légendaire : Vous pouvez utiliser votre action pour adopter une posture défensive. Vous gagnez un bonus de +4 à votre CA jusqu\'au début de votre prochain tour.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(37, 6, 'École de duel', 'L\'archétype École de duel se concentre sur le combat au corps à corps et la précision. Les guerriers de cet archétype sont des duellistes et peuvent utiliser des tactiques de duel.', NULL, NULL, 'Maîtrise du duel : Vous gagnez la maîtrise des armes de duel. De plus, vous gagnez un style de combat supplémentaire.', NULL, 'Tactiques de duel : Vous pouvez utiliser votre action pour adopter une posture de duel. Vous gagnez un bonus de +2 aux jets d\'attaque avec les armes de duel jusqu\'au début de votre prochain tour.', NULL, NULL, 'Style de combat supplémentaire : Vous gagnez un style de combat supplémentaire. Vous ne pouvez pas prendre le même style de combat plus d\'une fois, même si vous avez d\'autres occasions de le faire.', NULL, NULL, NULL, 'Maîtrise du duel supérieure : Vous pouvez utiliser votre action pour adopter une posture de duel. Vous gagnez un bonus de +3 aux jets d\'attaque avec les armes de duel jusqu\'au début de votre prochain tour.', NULL, 'Duelliste légendaire : Vous pouvez utiliser votre action pour adopter une posture de duel. Vous gagnez un bonus de +4 aux jets d\'attaque avec les armes de duel jusqu\'au début de votre prochain tour.', NULL, '2025-09-18 19:05:42', '2025-09-18 19:05:42'),
(39, 7, 'École d\'Abjuration', 'L\'École d\'Abjuration se concentre sur la magie de protection et de défense. Les magiciens de cette école sont des maîtres de la protection magique et peuvent créer des barrières magiques puissantes.', NULL, 'Maîtrise de l\'abjuration : Vous gagnez la maîtrise de l\'abjuration. De plus, vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts.', NULL, 'Amélioration de l\'abjuration : Quand vous lancez un sort d\'abjuration de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de l\'abjuration supérieure : Vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts. Le bouclier dure 1 minute.', NULL, NULL, 'Maître de l\'abjuration : Vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts. Le bouclier dure 10 minutes.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(40, 7, 'École de Conjuration', 'L\'École de Conjuration se concentre sur la magie de convocation et de téléportation. Les magiciens de cette école sont des maîtres de la convocation et peuvent invoquer des créatures et des objets.', NULL, 'Maîtrise de la conjuration : Vous gagnez la maîtrise de la conjuration. De plus, vous pouvez utiliser votre action pour invoquer un familier mineur.', NULL, 'Amélioration de la conjuration : Quand vous lancez un sort de conjuration de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de la conjuration supérieure : Vous pouvez utiliser votre action pour invoquer un familier mineur. Le familier dure 1 heure.', NULL, NULL, 'Maître de la conjuration : Vous pouvez utiliser votre action pour invoquer un familier mineur. Le familier dure 8 heures.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(41, 7, 'École de Divination', 'L\'École de Divination se concentre sur la magie de divination et de prédiction. Les magiciens de cette école sont des maîtres de la divination et peuvent voir l\'avenir et révéler des secrets.', NULL, 'Maîtrise de la divination : Vous gagnez la maîtrise de la divination. De plus, vous pouvez utiliser votre action pour lancer un sort de divination de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de la divination : Quand vous lancez un sort de divination de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de la divination supérieure : Vous pouvez utiliser votre action pour lancer un sort de divination de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de la divination : Vous pouvez utiliser votre action pour lancer un sort de divination de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(42, 7, 'École d\'Enchantement', 'L\'École d\'Enchantement se concentre sur la magie d\'enchantement et de charme. Les magiciens de cette école sont des maîtres de l\'enchantement et peuvent influencer les esprits et les émotions.', NULL, 'Maîtrise de l\'enchantement : Vous gagnez la maîtrise de l\'enchantement. De plus, vous pouvez utiliser votre action pour lancer un sort d\'enchantement de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de l\'enchantement : Quand vous lancez un sort d\'enchantement de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de l\'enchantement supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'enchantement de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de l\'enchantement : Vous pouvez utiliser votre action pour lancer un sort d\'enchantement de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(43, 7, 'École d\'Évocation', 'L\'École d\'Évocation se concentre sur la magie d\'évocation et de destruction. Les magiciens de cette école sont des maîtres de l\'évocation et peuvent invoquer des énergies destructrices.', NULL, 'Maîtrise de l\'évocation : Vous gagnez la maîtrise de l\'évocation. De plus, vous pouvez utiliser votre action pour lancer un sort d\'évocation de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de l\'évocation : Quand vous lancez un sort d\'évocation de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de l\'évocation supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'évocation de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de l\'évocation : Vous pouvez utiliser votre action pour lancer un sort d\'évocation de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(44, 7, 'École d\'Illusion', 'L\'École d\'Illusion se concentre sur la magie d\'illusion et de tromperie. Les magiciens de cette école sont des maîtres de l\'illusion et peuvent créer des images et des sons trompeurs.', NULL, 'Maîtrise de l\'illusion : Vous gagnez la maîtrise de l\'illusion. De plus, vous pouvez utiliser votre action pour lancer un sort d\'illusion de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de l\'illusion : Quand vous lancez un sort d\'illusion de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de l\'illusion supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'illusion de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de l\'illusion : Vous pouvez utiliser votre action pour lancer un sort d\'illusion de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(45, 7, 'École de Nécromancie', 'L\'École de Nécromancie se concentre sur la magie de nécromancie et de mort. Les magiciens de cette école sont des maîtres de la nécromancie et peuvent manipuler la vie et la mort.', NULL, 'Maîtrise de la nécromancie : Vous gagnez la maîtrise de la nécromancie. De plus, vous pouvez utiliser votre action pour lancer un sort de nécromancie de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de la nécromancie : Quand vous lancez un sort de nécromancie de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de la nécromancie supérieure : Vous pouvez utiliser votre action pour lancer un sort de nécromancie de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de la nécromancie : Vous pouvez utiliser votre action pour lancer un sort de nécromancie de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43');
INSERT INTO `class_archetypes` (`id`, `class_id`, `name`, `description`, `level_1_feature`, `level_2_feature`, `level_3_feature`, `level_6_feature`, `level_7_feature`, `level_8_feature`, `level_9_feature`, `level_10_feature`, `level_11_feature`, `level_13_feature`, `level_14_feature`, `level_15_feature`, `level_17_feature`, `level_18_feature`, `level_20_feature`, `created_at`, `updated_at`) VALUES
(46, 7, 'École de Transmutation', 'L\'École de Transmutation se concentre sur la magie de transmutation et de transformation. Les magiciens de cette école sont des maîtres de la transmutation et peuvent transformer la matière et l\'énergie.', NULL, 'Maîtrise de la transmutation : Vous gagnez la maîtrise de la transmutation. De plus, vous pouvez utiliser votre action pour lancer un sort de transmutation de niveau 1 sans dépenser d\'emplacement de sort.', NULL, 'Amélioration de la transmutation : Quand vous lancez un sort de transmutation de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, 'Maîtrise de la transmutation supérieure : Vous pouvez utiliser votre action pour lancer un sort de transmutation de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, 'Maître de la transmutation : Vous pouvez utiliser votre action pour lancer un sort de transmutation de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, NULL, '2025-09-18 19:31:43', '2025-09-18 19:31:43'),
(54, 8, 'Voie de l\'Ombre', 'La Voie de l\'Ombre se concentre sur la discrétion et l\'infiltration. Les moines de cette tradition sont des maîtres de l\'ombre et peuvent se déplacer dans les ténèbres avec une facilité surnaturelle.', NULL, NULL, 'Arts de l\'ombre : Vous gagnez la maîtrise de l\'ombre. De plus, vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration de l\'ombre : Quand vous lancez un sort d\'ombre de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise de l\'ombre supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître de l\'ombre : Vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(55, 8, 'Voie des Quatre Éléments', 'La Voie des Quatre Éléments se concentre sur la maîtrise des éléments. Les moines de cette tradition sont des maîtres des éléments et peuvent invoquer la puissance de la terre, de l\'air, du feu et de l\'eau.', NULL, NULL, 'Maîtrise des éléments : Vous gagnez la maîtrise des éléments. De plus, vous pouvez utiliser votre action pour lancer un sort d\'élément de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration des éléments : Quand vous lancez un sort d\'élément de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise des éléments supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'élément de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître des éléments : Vous pouvez utiliser votre action pour lancer un sort d\'élément de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(56, 8, 'Voie du Soleil', 'La Voie du Soleil se concentre sur la lumière et la guérison. Les moines de cette tradition sont des maîtres de la lumière et peuvent invoquer la puissance du soleil pour guérir et protéger.', NULL, NULL, 'Maîtrise du soleil : Vous gagnez la maîtrise du soleil. De plus, vous pouvez utiliser votre action pour lancer un sort de lumière de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration du soleil : Quand vous lancez un sort de lumière de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise du soleil supérieure : Vous pouvez utiliser votre action pour lancer un sort de lumière de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître du soleil : Vous pouvez utiliser votre action pour lancer un sort de lumière de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(57, 8, 'Voie de la Longue Mort', 'La Voie de la Longue Mort se concentre sur la mort et la nécromancie. Les moines de cette tradition sont des maîtres de la mort et peuvent manipuler l\'énergie vitale et la mort.', NULL, NULL, 'Maîtrise de la mort : Vous gagnez la maîtrise de la mort. De plus, vous pouvez utiliser votre action pour lancer un sort de mort de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration de la mort : Quand vous lancez un sort de mort de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise de la mort supérieure : Vous pouvez utiliser votre action pour lancer un sort de mort de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître de la mort : Vous pouvez utiliser votre action pour lancer un sort de mort de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(58, 8, 'Voie de la Paume Ouverte', 'La Voie de la Paume Ouverte se concentre sur la paix et la non-violence. Les moines de cette tradition sont des maîtres de la paix et peuvent désarmer et neutraliser leurs ennemis sans les blesser.', NULL, NULL, 'Maîtrise de la paix : Vous gagnez la maîtrise de la paix. De plus, vous pouvez utiliser votre action pour lancer un sort de paix de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration de la paix : Quand vous lancez un sort de paix de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise de la paix supérieure : Vous pouvez utiliser votre action pour lancer un sort de paix de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître de la paix : Vous pouvez utiliser votre action pour lancer un sort de paix de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(59, 8, 'Voie du Dragon', 'La Voie du Dragon se concentre sur la puissance et la sagesse des dragons. Les moines de cette tradition sont des maîtres du dragon et peuvent invoquer la puissance ancestrale des dragons.', NULL, NULL, 'Maîtrise du dragon : Vous gagnez la maîtrise du dragon. De plus, vous pouvez utiliser votre action pour lancer un sort de dragon de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration du dragon : Quand vous lancez un sort de dragon de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise du dragon supérieure : Vous pouvez utiliser votre action pour lancer un sort de dragon de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître du dragon : Vous pouvez utiliser votre action pour lancer un sort de dragon de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(60, 8, 'Voie de l\'Astral', 'La Voie de l\'Astral se concentre sur la projection astrale et la transcendance. Les moines de cette tradition sont des maîtres de l\'astral et peuvent projeter leur conscience dans le plan astral.', NULL, NULL, 'Maîtrise de l\'astral : Vous gagnez la maîtrise de l\'astral. De plus, vous pouvez utiliser votre action pour lancer un sort d\'astral de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration de l\'astral : Quand vous lancez un sort d\'astral de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise de l\'astral supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'astral de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître de l\'astral : Vous pouvez utiliser votre action pour lancer un sort d\'astral de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(61, 8, 'Voie de la Miséricorde', 'La Voie de la Miséricorde se concentre sur la guérison et la compassion. Les moines de cette tradition sont des maîtres de la miséricorde et peuvent guérir et protéger leurs alliés.', NULL, NULL, 'Maîtrise de la miséricorde : Vous gagnez la maîtrise de la miséricorde. De plus, vous pouvez utiliser votre action pour lancer un sort de miséricorde de niveau 1 sans dépenser d\'emplacement de sort.', 'Amélioration de la miséricorde : Quand vous lancez un sort de miséricorde de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.', NULL, NULL, NULL, NULL, 'Maîtrise de la miséricorde supérieure : Vous pouvez utiliser votre action pour lancer un sort de miséricorde de niveau 2 sans dépenser d\'emplacement de sort.', NULL, NULL, NULL, 'Maître de la miséricorde : Vous pouvez utiliser votre action pour lancer un sort de miséricorde de niveau 3 sans dépenser d\'emplacement de sort.', NULL, NULL, '2025-09-18 19:51:25', '2025-09-18 19:51:25'),
(69, 9, 'Pacte de la Chaîne', 'Le Pacte de la Chaîne vous lie à un familier surnaturel. Votre familier est plus puissant qu\'un familier normal et peut prendre des formes plus exotiques.', NULL, NULL, 'Familiar surnaturel : Vous obtenez un familier surnaturel qui peut prendre des formes plus puissantes qu\'un familier normal. Votre familier peut attaquer et a des capacités spéciales.', NULL, 'Amélioration du familier : Votre familier devient plus puissant et peut prendre des formes plus exotiques. Il gagne également des capacités spéciales supplémentaires.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise du familier : Votre familier devient un compagnon de combat redoutable. Il peut attaquer deux fois par tour et a des capacités magiques puissantes.', NULL, NULL, 'Maître du familier : Votre familier devient une extension de votre volonté. Il peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(70, 9, 'Pacte de la Lame', 'Le Pacte de la Lame vous lie à une arme magique. Votre arme de pacte est plus puissante qu\'une arme normale et peut être invoquée à volonté.', NULL, NULL, 'Arme de pacte : Vous obtenez une arme de pacte magique que vous pouvez invoquer à volonté. Cette arme est plus puissante qu\'une arme normale et peut être utilisée comme foyer d\'incantation.', NULL, 'Amélioration de l\'arme : Votre arme de pacte devient plus puissante et peut être utilisée pour lancer des sorts. Elle gagne également des capacités spéciales.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise de l\'arme : Votre arme de pacte devient une arme légendaire. Elle peut infliger des dégâts supplémentaires et a des capacités magiques puissantes.', NULL, NULL, 'Maître de l\'arme : Votre arme de pacte devient une extension de votre âme. Elle peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(71, 9, 'Pacte du Tome', 'Le Pacte du Tome vous lie à un grimoire magique. Votre grimoire contient des sorts supplémentaires et peut être utilisé pour lancer des sorts de n\'importe quelle classe.', NULL, NULL, 'Grimoire de sorts : Vous obtenez un grimoire magique qui contient des sorts supplémentaires. Vous pouvez ajouter des sorts de n\'importe quelle classe à votre grimoire.', NULL, 'Amélioration du grimoire : Votre grimoire devient plus puissant et peut contenir plus de sorts. Vous pouvez également lancer des sorts de niveau supérieur.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise du grimoire : Votre grimoire devient un artefact magique puissant. Il peut lancer des sorts automatiquement et a des capacités de divination.', NULL, NULL, 'Maître du grimoire : Votre grimoire devient une extension de votre esprit. Il peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(72, 9, 'Pacte du Talisman', 'Le Pacte du Talisman vous lie à un talisman magique. Votre talisman peut être donné à un allié pour lui conférer des capacités magiques.', NULL, NULL, 'Talisman magique : Vous obtenez un talisman magique qui peut être donné à un allié. Le porteur du talisman gagne des capacités magiques et des bonus aux jets de sauvegarde.', NULL, 'Amélioration du talisman : Votre talisman devient plus puissant et peut conférer plus de capacités. Il peut également être utilisé pour lancer des sorts.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise du talisman : Votre talisman devient un artefact magique puissant. Il peut lancer des sorts automatiquement et a des capacités de protection.', NULL, NULL, 'Maître du talisman : Votre talisman devient une extension de votre volonté. Il peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(73, 9, 'Pacte de l\'Épée', 'Le Pacte de l\'Épée vous lie à une épée magique. Votre épée de pacte est plus puissante qu\'une épée normale et peut être invoquée à volonté.', NULL, NULL, 'Épée de pacte : Vous obtenez une épée de pacte magique que vous pouvez invoquer à volonté. Cette épée est plus puissante qu\'une épée normale et peut être utilisée comme foyer d\'incantation.', NULL, 'Amélioration de l\'épée : Votre épée de pacte devient plus puissante et peut être utilisée pour lancer des sorts. Elle gagne également des capacités spéciales.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise de l\'épée : Votre épée de pacte devient une épée légendaire. Elle peut infliger des dégâts supplémentaires et a des capacités magiques puissantes.', NULL, NULL, 'Maître de l\'épée : Votre épée de pacte devient une extension de votre âme. Elle peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(74, 9, 'Pacte du Bouclier', 'Le Pacte du Bouclier vous lie à un bouclier magique. Votre bouclier de pacte est plus puissant qu\'un bouclier normal et peut être invoqué à volonté.', NULL, NULL, 'Bouclier de pacte : Vous obtenez un bouclier de pacte magique que vous pouvez invoquer à volonté. Ce bouclier est plus puissant qu\'un bouclier normal et peut être utilisé comme foyer d\'incantation.', NULL, 'Amélioration du bouclier : Votre bouclier de pacte devient plus puissant et peut être utilisé pour lancer des sorts. Il gagne également des capacités spéciales.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise du bouclier : Votre bouclier de pacte devient un bouclier légendaire. Il peut absorber plus de dégâts et a des capacités magiques puissantes.', NULL, NULL, 'Maître du bouclier : Votre bouclier de pacte devient une extension de votre âme. Il peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(75, 9, 'Pacte de la Couronne', 'Le Pacte de la Couronne vous lie à une couronne magique. Votre couronne de pacte est plus puissante qu\'une couronne normale et peut être invoquée à volonté.', NULL, NULL, 'Couronne de pacte : Vous obtenez une couronne de pacte magique que vous pouvez invoquer à volonté. Cette couronne est plus puissante qu\'une couronne normale et peut être utilisée comme foyer d\'incantation.', NULL, 'Amélioration de la couronne : Votre couronne de pacte devient plus puissante et peut être utilisée pour lancer des sorts. Elle gagne également des capacités spéciales.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise de la couronne : Votre couronne de pacte devient une couronne légendaire. Elle peut conférer des bonus supplémentaires et a des capacités magiques puissantes.', NULL, NULL, 'Maître de la couronne : Votre couronne de pacte devient une extension de votre âme. Elle peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(76, 9, 'Pacte de l\'Ange', 'Le Pacte de l\'Ange vous lie à un ange gardien. Votre ange de pacte est plus puissant qu\'un ange normal et peut être invoqué à volonté.', NULL, NULL, 'Ange de pacte : Vous obtenez un ange de pacte magique que vous pouvez invoquer à volonté. Cet ange est plus puissant qu\'un ange normal et peut être utilisé comme foyer d\'incantation.', NULL, 'Amélioration de l\'ange : Votre ange de pacte devient plus puissant et peut être utilisé pour lancer des sorts. Il gagne également des capacités spéciales.', NULL, NULL, NULL, NULL, NULL, NULL, 'Maîtrise de l\'ange : Votre ange de pacte devient un ange légendaire. Il peut infliger des dégâts supplémentaires et a des capacités magiques puissantes.', NULL, NULL, 'Maître de l\'ange : Votre ange de pacte devient une extension de votre âme. Il peut lancer des sorts en votre nom et a des capacités de télépathie avec vous.', '2025-09-18 20:16:26', '2025-09-18 20:16:26'),
(84, 10, 'Serment de Dévotion', 'Le serment de dévotion lie un paladin aux plus hautes idéaux de justice, de vertu et d\'ordre. Ces paladins se tiennent fermement à leurs principes et sont prêts à sacrifier leur vie pour défendre les innocents.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(85, 10, 'Serment des Anciens', 'Le serment des anciens est aussi ancien que la race des elfes et les rituels des premiers druides. Parfois appelés fey knights, green knights ou horned knights, les paladins qui prêtent ce serment s\'engagent à servir la lumière et la vie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(86, 10, 'Serment de Vengeance', 'Le serment de vengeance est un serment solennel de punir ceux qui ont commis un grand mal. Quand les forces terribles menacent les innocents, quand les tyrans oppriment les faibles, quand les cultes de démons et de morts-vivants se répandent dans le monde, les paladins qui prêtent ce serment s\'engagent à les détruire.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(87, 10, 'Serment de Conquête', 'Le serment de conquête est prêté par les paladins qui cherchent à dominer le monde par la force. Ces paladins sont souvent des tyrans ou des conquérants qui utilisent leur pouvoir divin pour imposer leur volonté.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(88, 10, 'Serment de Rédemption', 'Le serment de rédemption est prêté par les paladins qui croient que tout être peut être racheté et que la violence n\'est jamais la solution. Ces paladins cherchent à résoudre les conflits par la diplomatie et la compassion.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(89, 10, 'Serment de Gloire', 'Le serment de gloire est prêté par les paladins qui cherchent à accomplir de grands exploits et à être reconnus pour leurs actes héroïques. Ces paladins sont souvent des champions qui participent à des tournois et des quêtes épiques.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(90, 10, 'Serment de la Couronne', 'Le serment de la couronne lie un paladin à la loyauté envers un souverain, un gouvernement ou un ensemble de lois. Ces paladins servent comme gardiens de l\'ordre et de la justice dans leur royaume.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(91, 10, 'Serment de l\'Oathbreaker', 'Le serment de l\'oathbreaker est prêté par les paladins qui ont rompu leur serment sacré et se sont tournés vers le mal. Ces paladins ont perdu leur connexion divine mais ont gagné des pouvoirs sombres en échange.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2025-09-19 05:37:34', '2025-10-13 18:55:52'),
(99, 11, 'Chasseur', 'L\'archétype Chasseur se concentre sur l\'élimination efficace des ennemis et la survie dans la nature sauvage.', NULL, NULL, 'Chasseur de monstres', NULL, 'Défense contre les ennemis', NULL, NULL, NULL, 'Attaque multi-cibles', NULL, NULL, 'Superior Hunter\'s Defense', NULL, NULL, NULL, '2025-09-19 06:49:05', '2025-10-13 18:55:52'),
(100, 11, 'Garde des Bêtes', 'Le Garde des Bêtes forme un lien profond avec les créatures de la nature, combattant aux côtés d\'un compagnon animal.', NULL, NULL, 'Compagnon animal', NULL, 'Bond avec la nature', NULL, NULL, NULL, 'Attaque coordonnée', NULL, NULL, 'Superior Beast\'s Defense', NULL, NULL, NULL, '2025-09-19 06:49:05', '2025-10-13 18:55:52'),
(101, 11, 'Gloom Stalker', 'Le Gloom Stalker est un expert du combat dans l\'obscurité et les environnements sombres.', NULL, NULL, 'Gloom Stalker Magic', NULL, 'Iron Mind', NULL, NULL, NULL, 'Stalker\'s Fury', NULL, NULL, 'Shadowy Dodge', NULL, NULL, NULL, '2025-09-19 06:49:05', '2025-10-13 18:55:52'),
(102, 11, 'Horizon Walker', 'L\'Horizon Walker protège le monde matériel contre les menaces des autres plans d\'existence.', NULL, NULL, 'Horizon Walker Magic', NULL, 'Planar Warrior', NULL, NULL, NULL, 'Distant Strike', NULL, NULL, 'Spectral Defense', NULL, NULL, NULL, '2025-09-19 06:49:05', '2025-10-13 18:55:52'),
