INSERT INTO `poisons` (`id`, `csv_id`, `nom`, `cle`, `description`, `type`, `source`, `created_at`) VALUES
(1, '0', 'Ajida', 'ajida ', 'Liquide inodore et incolore, ce poison à retardement peut faire perdre la vue définitivement.\n\n', 'ingestion (mixture) - 1000 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(2, '1', 'Ajonc à feuilles bleues', 'ajonc-a-feuilles-bleues ', 'Cette plante typique à cause de la couleur de ses feuilles est aussi un poison qui en plus de sa couleur dégage une odeur si caractéristique qu\'un herboriste le reconnaîtra immédiatement.\n\n', 'blessure (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(3, '2', 'Amnésite', 'amnesite ', 'Ce poison végétal de couleur blanche tiré d\'une plante d\'altitude cause en quelques secondes un choc métabolique pouvant provoquer de graves lésions dans le cerveau.\n\n', 'ingestion (végétal) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(4, '3', 'Arbre à poison', 'arbre-a-poison ', 'L\'arbre à poison est une plante rare qui ne pousse qu\'au cœur des jungles tropicales. Pour se protéger des insectes, cet arbre produit un puissant poison orangé ; la moindre égratignure provoque d\'atroces douleurs dans la plaie. Ses fleurs sont le seul antidote naturel connu.\n\n', 'blessure (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(5, '4', 'Arsenic', 'arsenic ', 'Poison mortel sans odeur se présentant sous la forme d\'un liquide incolore.\n\n', 'ingestion (toxine) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(6, '5', 'Baiser de sorcière', 'baiser-de-sorciere ', 'Ce mélange de plusieurs toxines agit dans tout l\'organisme. Il se présente sous la forme d\'une gélatine brune.\n\n', 'contact (toxine) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(7, '6', 'Belladone', 'belladone ', 'Cette préparation à base des baies noires de belladone provoque dans un premier temps des troubles visuels (dilatation des pupilles) puis une accélération du rythme cardiaque pouvant causer la mort sur un sujet délicat. Elle se présente sous la forme d\'un liquide noir qui passe cependant relativement inaperçu dans des aliments ou dans du vin.\n\n', 'ingestion (végétal) - 250 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(8, '7', 'Bile de dragon vert', 'bile-de-dragon-vert ', 'Ce gaz lourd verdâtre est l\'un des poisons de masse les plus mortels. Heureusement, son prix, dû à la difficulté de rencontrer la substance de base, en fait un poison extrêmement rare.\n\n', 'inhalation (toxine) - 2500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(9, '8', 'Bluluka', 'bluluka ', 'Cette substance bleuâtre à l\'odeur aigre est un acide à action lente qui s\'attaque aux tissus de la peau.\n\n', 'contact (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(10, '9', 'Bralia', 'bralia ', 'La bralia se présente sous la forme d\'un liquide crémeux inodore mais qui possède un goût acidulé caractéristique. Peu dangereux, elle est plus utilisée plus mettre en garde que pour tuer.\n\n', 'ingestion (mixture) - 250 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(11, '10', 'Brume de folie', 'brume-de-folie ', 'La victime qui respire l\'odeur de parfum dégagé par cette potion se met dans un état nerveux proche de la folie qui se termine souvent par le suicide.\n\n', 'inhalation (mixture) - 2000 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(12, '11', 'Caresse de sorcière', 'caresse-de-sorciere ', 'Ce poison se présente sous la forme d\'un liquide incolore quasiment impossible à détecter visuellement. Il se lave facilement à l\'eau, ce qui le rend immédiatement inoffensif. Certains bijoutiers peu scrupuleux l\'utilisent sur leurs marchandises, dans leurs coffres-forts ou lorsqu\'ils voyagent dans des contrées peu sûres.\n\n', 'contact (toxine) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(13, '12', 'Caustar', 'caustar ', 'Le caustar est une crème de couleur jaune préparée à partir d\'herbes toxiques et il est souvent utilisé par des mercenaires au combat. Une fois que le poison est entré dans l\'organisme il provoque une intense douleur, non mortelle cependant.\n\n', 'blessure (végétal) - 250 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(14, '13', 'Cendres d\'ungol', 'cendres-d-ungol ', 'Ce poison se présente sous la forme de paillettes d\'un brun-roux poussiéreux. L\'odeur dégagée par celles-ci lorsqu\'elles se consument provoque de graves lésions respiratoires dont on ne se remet jamais tout à fait complètement.\n\n', 'inhalation (mixture) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(15, '14', 'Cendres de liche', 'cendres-de-liche ', 'Ce sirop noir au léger goût de cendres peut neutraliser sa victime en quelques secondes. Il est généralement ajouté aux aliments de la victime et quelques gouttes suffisent, ce qui explique son faible prix s\'agissant d\'une mixture.\n\n', 'ingestion (mixture) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(16, '15', 'Coulabine', 'coulabine ', 'Ce gaz grisâtre très fin, une fois libéré, provoque une forte accélération de la respiration chez la ou les victimes. Toute action plus fatigante qu\'une marche lente est généralement alors impossible.\n\n', 'inhalation (toxine) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(17, '16', 'Curare', 'curare ', 'Ce poison tiré d\'une plante tropicale provoque une paralysie des muscles.\n\n', 'blessure (végétal) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(18, '17', 'Cyanure', 'cyanure ', 'Ce poison de couleur noirâtre a une odeur caractéristique d\'amandes amères. Vendu en poudre semblable à du sel, il cause la mort en quelques secondes seulement.\n\n', 'ingestion (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(19, '18', 'Dernier verre', 'dernier-verre ', 'Ce poison est préparé pour être mélangé à du vin. Il n\'en altère pas le goût mais en éclaircit très légèrement la couleur. Il réagit avec les acides digestifs et provoque nausée, convulsions et crampes abdominales. La victime est ensuite immunisée à ce poison pendant une semaine.\n\n', 'ingestion (toxine) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(20, '19', 'Diffenbache', 'diffenbache ', 'Cette mixture complexe provoque la relaxation complète des cordes vocales de la victime qui ne peut alors plus parler. C\'est un liquide jaunâtre.\n\n', 'ingestion (mixture) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(21, '20', 'Doshenkana', 'doshenkana ', 'Ce poison se présente sous la forme d\'une fine poudre verdâtre. Il donne le souffle court et provoque nausées, perte de vision et crises de convulsions.\n\n', 'ingestion (toxine) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(22, '21', 'Droon', 'droon ', 'Ce poison liquide de couleur rouge est extrêmement puissant mais toutefois très instable (faible DD). C\'est tout ou rien.\n\n', 'blessure (toxine) - 1600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(23, '22', 'Emloc', 'emloc ', 'Ce poison qui ressemble à du sirop possède un fort goût sucré et une odeur de pêche. Il provoque la dégradation des parois du système sanguin. L\'effet se produit environ une demi-heure après l\'ingestion.\n\n', 'ingestion (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(24, '23', 'Entolome zébré', 'entolome-zebre ', 'Ce poison gris est tiré d\'un champignon blanc et noir qui ne vit que dans les profondeurs de la terre. Il provoque de graves troubles neurologiques au moment de la digestion.\n\n', 'ingestion (végétal) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(25, '24', 'Envie du désert', 'envie-du-desert ', 'Ce poison liquide de couleur verte est réalisé à base d\'une variété spéciale de cactus et cause une forte déshydratation (soif intense, fatigue, vertiges).\n\n', 'ingestion (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(26, '25', 'Enzyme de triple-fleur', 'enzyme-de-triple-fleur ', 'Ce liquide jaune collant inflige des dégâts au contact de la peau s\'il n\'est pas neutralisé. On peut facilement l\'enlever en le lavant, mais les points de vie perdus ne sont pas récupérés automatiquement.\n\n', 'contact (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(27, '26', 'Erwurg', 'erwurg ', 'Ce poison liquide vert clair, tiré d\'une fleur des bois, provoque une forte toux.\n\n', 'ingestion (végétal) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(28, '27', 'Essence d\'ombre', 'essence-d-ombre ', 'Ce poison noir élaboré exclusivement par des clercs à partir d\'essences de cette créature morte-vivante est très puissant. On ne s\'en remet jamais complètement.\n\n', 'blessure (mixture) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(29, '28', 'Essence éthérée', 'essence-etheree ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 15 ou être empoisonnée durant 8 heures. La créature empoisonnée est inconsciente et se réveille si elle subit des dégâts ou si une autre créature prend une action pour la secouer et ainsi la réveiller.\n\n', 'inhalation (toxine) - 300 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(30, '29', 'Extrait de lotus noir', 'extrait-de-lotus-noir ', 'Certainement le poison le plus dangereux au monde. Heureusement c\'est également le plus difficile à trouver du fait de l\'extrême rareté de cette plante et du soin exceptionnel à donner à la préparation. Personne n\'y échappe et la mort est quasi instantanée. C\'est un liquide inodore, incolore et pratiquement invisible.\n\n', 'ingestion (végétal) - 4000 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(31, '30', 'Extrait de sanvert', 'extrait-de-sanvert ', 'Ce poison provoque de fortes nausées, immédiatement après chaque ingestion de liquide ou d\'aliment. Il se présente sous la forme d\'une poudre soluble.\n\n', 'ingestion (végétal) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(32, '31', 'Fleur voilée', 'fleur-voilee ', 'Cette petite herbe se trouve relativement facilement dans les prairies. Elle contient un petit poison paralysant qui inquiète toujours les propriétaires de troupeaux. Cependant le goût de cette herbe est très fort et très désagréable, ce qui permet souvent dès la première bouchée de nourriture de s\'apercevoir qu\'on s\'empoisonne.\n\n', 'ingestion (végétal) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(33, '32', 'Fluna', 'fluna ', 'Le fluna est un coléoptère des marais qui ne devient agressif que lors des périodes de reproduction. Son venin administré à hautes doses est très dangereux.\n\n', 'blessure (venin) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(34, '33', 'Fumées d\'othur', 'fumees-d-othur ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 13 ou prendre 10 (3d6) dégâts de poison, et doit répéter le jet de sauvegarde au début de chacun de ses tours. Pour chaque jet de sauvegarde successif raté, le personnage prend 3 (1d6) dégâts de poison. Après trois jets réussis, le poison cesse d\'agir.\n\n', 'inhalation (mixture) - 500 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(35, '34', 'Garaban', 'garaban ', 'Poison liquide de couleur rouge courant chez les chasseurs et qui endort rapidement sa victime. Malgré le fait que ce soit un poison de blessure, il sert très peu en combat. Les chasseurs l\'utilisent principalement avec des armes de jet pour faciliter leur approche.\n\n', 'blessure (toxine) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(36, '35', 'Gaz des marais', 'gaz-des-marais ', 'Ce gaz grisâtre affaiblit ses victimes en les ralentissant, voire en les endormant.\n\n', 'inhalation (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(37, '36', 'Gomme de jaraba', 'gomme-de-jaraba ', 'Ce poison apparaît sous la forme d\'une pâte caoutchouteuse de couleur gris clair. Mélangée à des aliments, il se dissout à la cuisson pour devenir indécelable par le goût ou l\'odeur.\n\n', 'ingestion (mixture) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(38, '37', 'Herbe aux loups', 'herbe-aux-loups ', 'Cette plante très toxique généralement utilisée pour tuer les renards et les loups peut être également mortelle pour un humanoïde. Bien préparée, elle sert aussi d\'antidote au venin des scorpions.\n\n', 'ingestion (végétal) - 250 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(39, '38', 'Herbe du diable', 'herbe-du-diable ', 'Si inhaler les vapeurs de cette plante suffit à provoquer de faibles hallucinations durant quelques heures, c\'est en l\'ingérant qu\'elle devient un vrai poison qui provoque de fortes hallucinations et une perte de la voix.\n\n', 'ingestion (végétal) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(40, '39', 'Huile de taggit', 'huile-de-taggit ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 13 ou être empoisonnée durant 24 heures. La créature empoisonnée est inconsciente et se réveille si elle subit des dégâts.\n\n', 'contact (toxine) - 400 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(41, '40', 'Huile noire', 'huile-noire ', 'Ce liquide noir n\'affecte que les mammifères. Il provoque la coloration en noir des pupilles de ses victimes, mais permet de mieux résister à la douleur. Certains diplomates ou espions l\'utilisent quand ils craignent d\'être torturés.\n\n', 'ingestion (mixture) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(42, '41', 'Illumination du moissonneur', 'illumination-du-moissonneur ', 'Une fois ce poison liquide et collant au contact du sang de sa victime, celle-ci perd temporairement l\'ouïe.\n\n', 'blessure (mixture) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(43, '42', 'Illusion de Lomat', 'illusion-de-lomat ', 'Liquide ou poudre de couleur sable sans goût qui une fois ingurgitée fait croire à la victime qu\'elle est épiée et surveillée en permanence.\n\n', 'ingestion (mixture) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(44, '43', 'Jalwun jiwin', 'jalwun-jiwin ', 'Comparé aux autres poisons utilisés par les elfes noirs, celui-ci semble n\'être qu\'un sympathique avertissement. Ce poison noir est préparé à partir du venin d\'un crapaud souterrain. La victime est prise de nausées, de fièvre et de vomissements.\n\n', 'blessure (venin) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(45, '44', 'Jatropha curcas', 'jatropha-curcas ', 'La menace de ces noix réside dans leur goût délicieux car celui qui en goûte une en remange instinctivement plusieurs autres. Au-delà d\'une dizaine de noix surviennent les crampes dans les jambes. L\'arbre ne pousse qu\'aux tropiques.\n\n', 'ingestion (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(46, '45', 'Kintal de mage', 'kintal-de-mage ', 'Ce poison qui nécessite entre autres de nombreux petits scorpions pour sa préparation coupe les cordes vocales de la victime. Cette substance est essentiellement employée contre les magiciens et autres lanceurs de sorts.\n\n', 'blessure (mixture) - 700 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(47, '46', 'Larmes de drider', 'larmes-de-drider ', 'Malgré son nom, c\'est de la salive de ces monstres qu\'est élaboré ce poison. Au contact du sang, ce poison fait augmenter le volume des globes oculaires, les yeux deviennent injectés de sang et des larmes coulent de couleur rouge sang.\n\n', 'blessure (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(48, '47', 'Larmes de minuit', 'larmes-de-minuit ', 'Une créature qui ingère ce poison ne souffre d\'aucun effet jusqu\'au coup de minuit. Si le poison n\'a pas été neutralisé avant, la créature doit réussir un jet de sauvegarde de Constitution DD 17, subissant 31 (9d6) dégâts de poison en cas d\'échec, ou la moitié de ces dégâts en cas de réussite.\n\n', 'ingestion (mixture) - 1500 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(49, '48', 'Malice', 'malice ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 15 ou être empoisonnée durant 1 heure. La créature empoisonnée est aveuglée.\n\n', 'inhalation (toxine) - 250 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(50, '49', 'Mandragore', 'mandragore ', 'À la base une plante bénéfique, en surdose la mandragore est un véritable poison au goût amer.\n\n', 'ingestion (végétal) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(51, '50', 'Marchand de sable', 'marchand-de-sable ', 'Ce poison sous forme de poudre induit un sommeil magique qui n\'affecte pas les elfes ou les demi-elfes.\n\n', 'blessure (mixture) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(52, '51', 'Mortelame', 'mortelame ', 'Ce puissant poison à base de venin d\'araignée est très prisé par les elfes noirs pour l\'assassinat. Il détruit les parois du système vasculaire causant des hémorragies internes massives. En quelques minutes les victimes perdent du sang par tous les orifices puis meurent. Ce poison n\'est fabriqué que par les elfes noirs.\n\n', 'blessure (toxine) - 1600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(53, '52', 'Mucus de charognard rampant', 'mucus-de-charognard-rampant ', 'Ce poison doit être récupéré sur un charognard rampant mort ou incapable d\'agir.\n\n', 'contact (venin) - 200 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(54, '53', 'Murfa', 'murfa ', 'Ce liquide incolore et inodore à base de plomb provoque d\'incontrôlables convulsions.\n\n', 'ingestion (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(55, '54', 'Nibon', 'nibon ', 'Ce liquide inodore et incolore à base d\'or et d\'argent rend complètement sourd durant quelques minutes.\n\n', 'ingestion (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(56, '55', 'Nitharite', 'nitharite ', 'Cette poudre ne fonctionne que si elle est lancée dans les yeux des victimes. Elle provoque immédiatement des brûlures qui gênent grandement la vision.\n\n', 'contact (végétal) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(57, '56', 'Oeil du basilic', 'oeil-du-basilic ', 'Ce poison dont l\'élément principal est un œil de basilic a un effet immédiat de pétrification.\n\n', 'blessure (mixture) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(58, '57', 'Opia', 'opia ', 'Cette poudre brune à l\'odeur de café se dissout dans une boisson et embrouille complètement l\'esprit.\n\n', 'ingestion (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(59, '58', 'Pâte de malyss', 'pate-de-malyss ', 'La pâte préparée à base des racines de cette plante, une fois en contact avec la peau, provoque des tremblements de tous les membres et un léger essoufflement.\n\n', 'contact (végétal) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(60, '59', 'Piedlent', 'piedlent ', 'Le piedlent est une herbe aux petites fleurs cotonneuses. Il donne un liquide vert clair qui, mélangé dans la nourriture, augmente le rythme de la respiration et donne une grande sensation de fatigue.\n\n', 'ingestion (végétal) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(61, '60', 'Poison d\'ettercap', 'poison-d-ettercap ', 'Ce poison doit être récupéré sur un ettercap mort ou incapable d\'agir.\n\n', 'blessure (venin) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(62, '61', 'Poison de crapaud géant', 'poison-de-crapaud-geant ', 'Ce poison doit être récupéré sur un crapaud géant mort ou incapable d\'agir.\n\n', 'blessure (venin) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(63, '62', 'Poison de ver pourpre', 'poison-de-ver-pourpre ', 'Ce poison doit être récupéré sur un ver pourpre mort ou incapable d\'agir.\n\n', 'blessure (venin) - 2000 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(64, '63', 'Poison de wiverne', 'poison-de-wiverne ', 'Ce poison doit être récupéré sur une wiverne morte ou incapable d\'agir.\n\n', 'blessure (venin) - 1200 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(65, '64', 'Poison drow', 'poison-drow ', 'Ce poison n\'est généralement confectionné que par les drows, et uniquement dans un endroit à l\'abri de la lumière du soleil.\n\n', 'blessure (toxine) - 200 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(66, '65', 'Pollen de cybella', 'pollen-de-cybella ', 'Respirer le pollen de cette fleur fait apparaître à la victime de nombreuses hallucinations (apparition de créatures ennemies, sentiment de persécutions et/ou d\'être épié).\n\n', 'inhalation (végétal) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(67, '66', 'Poudre d\'assonne', 'poudre-d-assonne ', 'Poudre blanche issus de feuilles séchées, ce poison « bon marché » qui peut être mortel pour de faibles créatures par simple contact est heureusement relativement détectable grâce à la forte odeur de fruits qu\'il dégage.\n\n', 'contact (végétal) - 200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(68, '67', 'Poudre de lotus jaune', 'poudre-de-lotus-jaune ', 'Ce poison de couleur jaune a un goût de citrique très acide. Il perturbe de manière définitive l\'élocution.\n\n', 'ingestion (végétal) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(69, '68', 'Poudre de lotus noir', 'poudre-de-lotus-noir ', 'Ce poison gris foncé a un goût très amer. On ne s\'en remet jamais complètement.\n\n', 'ingestion (végétal) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(70, '69', 'Quarigan', 'quarigan ', 'Cette substance est aussi appelée « ChopeNain » car elle n\'est toxique que pour cette race. Il s\'agit d\'une huile utilisée sur les armes et qui ne sèche qu\'au bout de quatre ou cinq jours. La légende dit que les elfes sont à l\'origine de ce poison.\n\n', 'blessure (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(71, '70', 'Queue de scorpion royal', 'queue-de-scorpion-royal ', 'Le venin de cet animal, que l\'on extrait de sa queue, agit par simple contact avec la peau.\n\n', 'contact (venin) - 1000 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(72, '71', 'Rêve d\'Ya', 'reve-d-ya ', 'Poudre ou liquide pourpre qui sent le raisin. Après son ingestion, la victime ne trouvera plus le sommeil.\n\n', 'ingestion (mixture) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(73, '72', 'Rhododendron', 'rhododendron ', 'Le poison à base de cette plante provoque de fortes diarrhées et des vomissements.\n\n', 'ingestion (végétal) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(74, '73', 'Salive de goule', 'salive-de-goule ', 'Cette huile verdâtre fabriquée par des clercs sent la pourriture et paralyse les membres inférieurs de ses victimes.\n\n', 'contact (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(75, '74', 'Sang d\'assassin', 'sang-d-assassin ', 'Une créature sujette à ce poison doit effectuer un jet de sauvegarde de Constitution DD 10. En cas d\'échec, elle subit 6 (1d12) dégâts de poison et est empoisonnée durant 24 heures. En cas de réussite, la créature ne subit que la moitié des dégâts et n\'est pas empoisonnée.\n\n', 'ingestion (toxine) - 150 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(76, '75', 'Sang de troll', 'sang-de-troll ', 'Ce fluide gris vert nécessite entre autres cinq gouttes de sang de troll pour sa préparation. Il devient complètement inoffensif s\'il est porté à une température supérieure à 40 degrés. L\'effet étant très court, l\'empoisonneur se tient généralement face à sa victime.\n\n', 'ingestion (toxine) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(77, '76', 'Sang des venins', 'sang-des-venins ', 'Ce mélange de venins (scorpions, crapauds et serpents géants) se présente sous forme d\'un liquide jaune. Il est plus instable (faible DD) mais plus puissant que les venins normaux.\n\n', 'blessure (venin) - 1200 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(78, '77', 'Sérum de vérité', 'serum-de-verite ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 11 ou être empoisonnée durant 1 heure. La créature empoisonnée ne peut pas dire un mensonge sciemment, comme sous l\'effet d\'un sort de <a href=\"https://www.aidedd.org/dnd/sorts.php?vf=zone-de-verite\">zone de vérité</a>.\n\n', 'ingestion (mixture) - 150 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(79, '78', 'Sirop de rêve', 'sirop-de-reve ', 'Liquide gris ou blanc au goût sucré, cette drogue procure des rêves très plaisants. Certaines personnes se l\'administrent volontairement.\n\n', 'ingestion (mixture) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(80, '79', 'Sombre serpent', 'sombre-serpent ', 'Ce poison est une poudre rouge carmin qui laisse à vie sa coloration sur les surfaces qu\'elle recouvre, avec un aspect de verrue.\n\n', 'contact (toxine) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(81, '80', 'Souffle du désert', 'souffle-du-desert ', 'Ce terrible poison magique donne une sensation de chaleur et fait transpirer sa victime de manière anormale.\n\n', 'inhalation (mixture) - 700 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(82, '81', 'Spores d\'ascomoïde', 'spores-d-ascomoide ', 'Cette poudre élaborée à base de champignons embrouille l\'esprit.\n\n', 'inhalation (végétal) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(83, '82', 'Teinture pale', 'teinture-pale ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 16 pour ne pas prendre 3 (1d6) dégâts de poison et devenir empoisonnée. La créature empoisonnée doit répéter le jet de sauvegarde toutes les 24 heures, subissant 3 (1d6) dégâts de poison en cas d\'échec. Jusqu\'à ce que le poison ne cesse d\'agir, les dégâts infligés par le poison ne peuvent être guéris d\'aucun moyen. Après sept réussites aux jets de sauvegarde, l\'effet prend fin et la créature peut guérir normalement.\n\n', 'ingestion (mixture) - 250 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(84, '83', 'Ténébreux vireux', 'tenebreux-vireux ', 'Ce champignon mortel réduit en poudre et mélangé à des aliments provoque, immédiatement après ingestion, de violentes douleurs abdominales conduisant à la mort dans la plupart des cas.\n\n', 'ingestion (végétal) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(85, '84', 'Terrinave', 'terrinave ', 'Cette huile issue des racines de terrinave provoque une sorte de paralysie temporelle du cerveau.\n\n', 'contact (végétal) - 500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(86, '85', 'Thrum', 'thrum ', 'Ce liquide bleu clair sent le citron. Il met la victime dans un état d\'alcoolisme très avancé.\n\n', 'ingestion (toxine) - 700 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(87, '86', 'Tormentille', 'tormentille ', 'Séchée et préparée correctement, cette racine devient un puissant poison lent. L\'effet n\'apparait en effet que plus d\'une heure après le contact.\n\n', 'blessure (végétal) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(88, '87', 'Torpeur', 'torpeur ', 'Une créature sujette à ce poison doit réussir un jet de sauvegarde de Constitution DD 15 ou être empoisonnée durant 4d6 heures. La créature empoisonnée est incapable d\'agir.\n\n', 'ingestion (mixture) - 600 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:47'),
(89, '88', 'Toxine d\'aspic', 'toxine-d-aspic ', 'Ce poison élaboré à base des glandes de ce serpent inflige peu de dégât, mais il est quasiment impossible d\'y résister.\n\n', 'blessure (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(90, '89', 'Toxine d\'oeil tueur', 'toxine-d-oeil-tueur ', 'Ce gaz particulièrement redoutable est préparé à partir du foie d\'un poisson de mer extrêmement rare appelé œil tueur. Une fois libéré, le nuage toxique tue la plupart des créatures qui le respirent.\n\n', 'inhalation (toxine) - 2500 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:47'),
(91, '90', 'Ubon', 'ubon ', 'Ce liquide jaune à base d\'or fondu, inodore mais au goût de melon acide, est redoutable.\n\n', 'ingestion (toxine) - 800 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(92, '91', 'Uropygus', 'uropygus ', 'Le gaz jaunâtre extrait de ce poison perturbe fortement la coordination et le système auditif, lequel influe sur l\'équilibre.\n\n', 'inhalation (toxine) - 600 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(93, '92', 'Venin d\'araignée géante', 'venin-d-araignee-geante ', 'Ce poison doit être récupéré sur une araignée géante morte ou incapable d\'agir.\n\n', 'blessure (venin) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(94, '93', 'Venin d\'araignée-loup géante', 'venin-d-araignee-loup-geante ', 'Ce poison doit être récupéré sur une araignée-loup géante morte ou incapable d\'agir.\n\n', 'blessure (venin) - 250 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(95, '94', 'Venin de guêpe géante', 'venin-de-guepe-geante ', 'Ce poison doit être récupéré sur une guêpe géante morte ou incapable d\'agir.\n\n', 'blessure (venin) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(96, '95', 'Venin de mille-pattes géant', 'venin-de-mille-pattes-geant ', 'Ce poison doit être récupéré sur un mille-pattes géant mort ou incapable d\'agir.\n\n', 'blessure (venin) - 300 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(97, '96', 'Venin de scorpion géant', 'venin-de-scorpion-geant ', 'Ce poison doit être récupéré sur un scorpion géant mort ou incapable d\'agir.\n\n', 'blessure (venin) - 1000 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(98, '97', 'Venin de serpent', 'venin-de-serpent ', 'Ce poison doit être récupéré sur un serpent venimeux géant mort ou incapable d\'agir.\n\n', 'blessure (venin) - 200 po', 'Dungeon Master´s Guide', '2025-09-03 19:26:48'),
(99, '98', 'Venin de vipère à tête noire', 'venin-de-vipere-a-tete-noire ', 'Ce poison doit être récupéré sur une vipère à tête noire incapable d\'agir mais encore vivante. Le venin de ce serpent agit à retardement.\n\n', 'blessure (venin) - 150 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48'),
(100, '99', 'Vin argenté', 'vin-argente ', 'Ce poison est une liqueur au goût très fin de couleur argentée. De manière complètement inexpliquée, il ne fonctionne pas sur les halfelins ni sur les gnomes.\n\n', 'ingestion (toxine) - 400 po', 'Recueil des poisons d´AideDD', '2025-09-03 19:26:48');
INSERT INTO `races` (`id`, `name`, `description`, `image`, `strength_bonus`, `dexterity_bonus`, `constitution_bonus`, `intelligence_bonus`, `wisdom_bonus`, `charisma_bonus`, `size`, `speed`, `vision`, `languages`, `traits`, `created_at`, `updated_at`) VALUES
(6, 'Haut-elfe', 'Elfe noble avec des capacités magiques innées', 'haut-elfe.jpg', 0, 2, 0, 1, 0, 0, 'M', 9, '', 'commun, elfique, une langue de votre choix', 'Sens aiguisés, Ascendance féerique (AV aux JdS vs charme et la magie ne peut pas vous endormir), Transe (4h de méditation remplacent 8h de sommeil), Entraînement aux armes elfiques, Sort mineur', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(7, 'Elfe des bois', 'Elfe adapté à la vie en forêt', 'elfe-des-bois.jpg', 0, 2, 0, 0, 1, 0, 'M', 10, 'Vision dans le noir (18 m)', 'commun, elfique', 'Sens aiguisés, Ascendance féerique (AV aux JdS vs charme et la magie ne peut pas vous endormir), Transe (4h de méditation remplacent 8h de sommeil), Entraînement aux armes elfiques, Foulée légère, Cachette naturelle (peut tenter de se cacher dans une zone à visibilité réduite)', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(8, 'Elfe noir', 'Elfe des profondeurs avec des pouvoirs magiques sombres', 'elfe-noir.jpg', 0, 2, 0, 0, 0, 1, 'M', 9, 'Vision dans le noir (18 m)', 'commun, elfique', 'Sens aiguisés, Ascendance féerique (AV aux JdS vs charme et la magie ne peut pas vous endormir), Transe (4h de méditation remplacent 8h de sommeil), Entraînement aux armes drows, Sensibilité au soleil, Magie drow', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(9, 'Halfelin pied-léger', 'Halfelin agile et discret', 'halfelin.jpg', 0, 2, 0, 0, 0, 1, 'P', 7, '', 'commun, halfelin', 'Chanceux (relancer un 1), Brave (AV aux JdS vs effrayé), Agilité halfeline (peut passer dans l\'espace d\'une créature de taille supérieure), Discrétion naturelle (peut tenter de se cacher derrière une créature de taille supérieure)', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(10, 'Halfelin robuste', 'Halfelin résistant et robuste', 'halfelin-robuste.jpg', 0, 2, 1, 0, 0, 1, 'P', 7, '', 'commun, halfelin', 'Chanceux (relancer un 1), Brave (AV aux JdS vs effrayé), Agilité halfeline (peut passer dans l\'espace d\'une créature de taille supérieure), Résistance des robustes (AV aux JdS contre le poison et résistance contre les dégâts de poison)', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(11, 'Humain', 'Race adaptable et polyvalente', 'humain.jpg', 1, 1, 1, 1, 1, 1, 'M', 9, '', 'commun, une langue de votre choix', 'Versatilité humaine', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(12, 'Nain des collines', 'Nain des collines, sage et résistant', 'nain.jpg', 0, 0, 2, 0, 1, 0, 'M', 7, 'Vision dans le noir (18 m)', 'commun, nain', 'Résistance naine (AV aux JdS vs poison), Entraînement aux armes naines, Maîtrise des outils, Connaissance de la pierre (bonus de maîtrise x2 aux jets d\'Int (Histoire) en relation avec la pierre), Ténacité naine (+1 pv/niveau)', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(13, 'Nain des montagnes', 'Nain des montagnes, fort et guerrier', 'nain-montagnes.jpg', 2, 0, 2, 0, 1, 0, 'M', 7, 'Vision dans le noir (18 m)', 'commun, nain', 'Résistance naine (AV aux JdS vs poison), Entraînement aux armes naines, Maîtrise des outils, Connaissance de la pierre (bonus de maîtrise x2 aux jets d\'Int (Histoire) en relation avec la pierre), Formation au port des armures naines', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(14, 'Demi-elfe', 'Hybride entre humain et elfe', 'demielfe.jpg', 0, 0, 0, 0, 0, 2, 'M', 9, 'Vision dans le noir (18 m)', 'commun, elfique, une langue de votre choix', 'Vision dans le noir, Ascendance féerique (AV aux JdS contre les effets de charme et la magie ne peut pas vous endormir), Polyvalence', '2025-09-12 18:45:35', '2025-09-21 19:04:11'),
(15, 'Demi-orc', 'Hybride entre humain et orc', 'demiorc.jpg', 2, 0, 1, 0, 0, 0, 'M', 9, 'Vision dans le noir (18 m)', 'commun, orc', 'Menaçant, Endurance implacable, Attaques sauvages', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(16, 'Drakéide', 'Descendant des dragons', 'drakeide.jpg', 2, 0, 0, 0, 0, 1, 'M', 9, '', 'commun, draconique', 'Ascendance draconique', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(17, 'Gnome des forêts', 'Gnome des forêts, rusé et magique', 'gnome.jpg', 0, 1, 0, 0, 0, 2, 'P', 7, 'Vision dans le noir (18 m)', 'commun, gnome', 'Ruse gnome, Communication avec les petits animaux, Illusionniste-né', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(18, 'Gnome des roches', 'Gnome des roches, ingénieux et inventif', 'gnome-profondeurs.jpg', 0, 0, 1, 0, 0, 2, 'P', 7, 'Vision dans le noir (18 m)', 'commun, gnome', 'Ruse gnome, Connaissance en ingénierie, Bricoleur', '2025-09-12 18:45:35', '2025-09-17 13:11:21'),
(19, 'Tieffelin', 'Descendant des créatures infernales', 'tieffelin.jpg', 0, 0, 0, 1, 0, 2, 'M', 9, 'Vision dans le noir (18 m)', 'commun, infernal', 'Résistance infernale (feu), Ascendance infernale', '2025-09-12 18:45:35', '2025-09-17 13:11:21');
INSERT INTO `rage_usage` (`character_id`, `rages_used`, `created_at`, `updated_at`) VALUES
(52, 0, '2025-09-23 19:33:45', '2025-09-23 19:33:45');
INSERT INTO `regions` (`id`, `country_id`, `name`, `description`, `map_url`, `created_at`, `updated_at`) VALUES
(1, 1, 'Province Centrale', 'Le cœur du royaume, où se trouve la capitale.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
(2, 1, 'Marches de l\'Est', 'Terres frontalières à l\'est, souvent en conflit.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
(3, 1, 'Forêts de l\'Ouest', 'Vastes forêts peuplées d\'elfes et de créatures magiques.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
(4, 2, 'Vallée des Dragons', 'Le centre de l\'empire, dominé par les anciens dragons.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
(5, 2, 'Montagnes de Feu', 'Chaîne de montagnes volcaniques, domaine des dragons rouges.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
(6, 2, 'Plaines de Glace', 'Terres gelées du nord, royaume des dragons blancs.', NULL, '2025-09-19 07:55:54', '2025-09-19 07:55:54'),
