INSERT INTO monk_traditions (name, description, level_3_feature, level_6_feature, level_11_feature, level_17_feature) VALUES
(
    'Voie de l\'Ombre',
    'La Voie de l\'Ombre se concentre sur la discrétion et l\'infiltration. Les moines de cette tradition sont des maîtres de l\'ombre et peuvent se déplacer dans les ténèbres avec une facilité surnaturelle.',
    'Arts de l\'ombre : Vous gagnez la maîtrise de l\'ombre. De plus, vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 1 sans dépenser d\'emplacement de sort.',
    'Amélioration de l\'ombre : Quand vous lancez un sort d\'ombre de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.',
    'Maîtrise de l\'ombre supérieure : Vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 2 sans dépenser d\'emplacement de sort.',
    'Maître de l\'ombre : Vous pouvez utiliser votre action pour lancer un sort d\'ombre de niveau 3 sans dépenser d\'emplacement de sort.'
),
(
