INSERT INTO wizard_traditions (name, description, level_2_feature, level_6_feature, level_10_feature, level_14_feature) VALUES
(
    'École d\'Abjuration',
    'L\'École d\'Abjuration se concentre sur la magie de protection et de défense. Les magiciens de cette école sont des maîtres de la protection magique et peuvent créer des barrières magiques puissantes.',
    'Maîtrise de l\'abjuration : Vous gagnez la maîtrise de l\'abjuration. De plus, vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts.',
    'Amélioration de l\'abjuration : Quand vous lancez un sort d\'abjuration de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.',
    'Maîtrise de l\'abjuration supérieure : Vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts. Le bouclier dure 1 minute.',
    'Maître de l\'abjuration : Vous pouvez utiliser votre action pour créer un bouclier d\'abjuration qui vous protège des dégâts. Le bouclier dure 10 minutes.'
),
(
    'École de Conjuration',
    'L\'École de Conjuration se concentre sur la magie de convocation et de téléportation. Les magiciens de cette école sont des maîtres de la convocation et peuvent invoquer des créatures et des objets.',
    'Maîtrise de la conjuration : Vous gagnez la maîtrise de la conjuration. De plus, vous pouvez utiliser votre action pour invoquer un familier mineur.',
    'Amélioration de la conjuration : Quand vous lancez un sort de conjuration de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.',
    'Maîtrise de la conjuration supérieure : Vous pouvez utiliser votre action pour invoquer un familier mineur. Le familier dure 1 heure.',
    'Maître de la conjuration : Vous pouvez utiliser votre action pour invoquer un familier mineur. Le familier dure 8 heures.'
),
(
    'École de Divination',
    'L\'École de Divination se concentre sur la magie de divination et de prédiction. Les magiciens de cette école sont des maîtres de la divination et peuvent voir l\'avenir et révéler des secrets.',
    'Maîtrise de la divination : Vous gagnez la maîtrise de la divination. De plus, vous pouvez utiliser votre action pour lancer un sort de divination de niveau 1 sans dépenser d\'emplacement de sort.',
    'Amélioration de la divination : Quand vous lancez un sort de divination de niveau 1 ou plus, vous pouvez lancer un d20. Sur un 1, la magie sauvage se déclenche.',
    'Maîtrise de la divination supérieure : Vous pouvez utiliser votre action pour lancer un sort de divination de niveau 2 sans dépenser d\'emplacement de sort.',
    'Maître de la divination : Vous pouvez utiliser votre action pour lancer un sort de divination de niveau 3 sans dépenser d\'emplacement de sort.'
),
(
